import "../../../styles/AddProduct/AddProduct.css"
import ReactBsForm from "react-bootstrap/Form";
import { SwitchBtn } from "../../../components/ReactElement/SwitchBtn"
import home from "../../../images/images/png/home.png"
import list from "../../../images/images/png/list.png"
import people from "../../../images/images/png/people.png"
import shopCart from "../../../images/images/png/shopCart.png"
import { UploadInput } from "../../../components/ReactElement/DataUpladBtn"

// 新增品項按鈕過來的web
export function AddProduct(props) {
    return <span className="AddProduct">
        <ReactBsForm className="ClassificationCap" onClick={(e) => {
            e.preventDefault()
            AppView();
        }}>
            <div className="ClassificationCapInput">
                <p>狀態</p>
                <SwitchBtn className="AddProductSwitchBtn" />
            </div>
            <div className="ClassificationCapInput">
                <p>商品編號</p>
                <input onChange={() => {
                    AppView();
                }} value={"A9487945"} placeholder="請輸入編號"></input>
            </div>
            <div className="ClassificationCapInput">
                <p>商品名稱</p>
                <input className="CartNameValue" placeholder="請輸入名稱"></input>
            </div>
            <div className="ClassificationCapInput">
                <p>分類</p>
                <select className="TypeSelect">
                    <option>請選擇</option>
                    <option>肉品</option>
                    <option>水果</option>
                    <option>人氣精選</option>
                </select>
            </div>
            <div className="ClassificationCapInput">
                <p>售價</p>
                <input onChange={() => {
                    AppView();
                }} className="SellingPrice"></input>
            </div>
            <UploadInput p="商品圖" specification="建議尺寸800px * 800px" />
            <div className="ClassificationCapInput">
                <p>商品說明</p>
                <textarea placeholder="請輸入內容" className="ExplanationTextarea"></textarea>
            </div>
        </ReactBsForm>
        <div className="PreviewPic">
            <label>APP畫面預覽</label>
            <div className="cellPhone">
                <div className="ScreenArea"></div>
                <div className="ShoppingCart">
                    <div className="CartType">商品分類 : </div>
                    <div className="CartName">商品名稱 : </div>
                    <div className="CartIndivoduce">商品說明 : </div>
                    <div className="CartPrice">{"$100"} </div>
                    <span className="CartAmount">
                        <div className="CartAmountBtn">-</div>
                        <div style={{
                            marginTop: "2px",
                            fontSize: "14px",
                            fontWeight: "bold",
                        }}>1</div>
                        <div className="CartAmountBtn">+</div>
                        <div className="MiniScreenArea"></div>
                    </span>
                </div>
                <div className="JoinCart">
                    <button className="CartBtn" variant="primary" type="submit">加入購物車</button>
                </div>
                <div className="PreviewIcons">
                    <span>
                        <img alt="" src={home}></img>
                        <h6>首頁</h6>
                    </span>
                    <span>
                        <img alt="" src={list}></img>
                        <h6>訂單</h6>
                    </span>
                    <span>
                        <img alt="" src={people}></img>
                        <h6>帳戶</h6>
                    </span>
                    <span>
                        <img alt="" src={shopCart}></img>
                        <h6>購物車</h6>
                    </span>
                </div>
            </div>
        </div>
    </span>
}

// APP畫面預覽文字與圖片輸入內容出現效果
export function AppView() {
    let CartNameValue = document.getElementsByClassName("CartNameValue")[0].value;
    let CartName = document.getElementsByClassName("CartName")[0];
    let TypeSelect = document.getElementsByClassName("TypeSelect")[0];
    let CartType = document.getElementsByClassName("CartType")[0];
    let SellingPrice = document.getElementsByClassName("SellingPrice")[0].value;
    let CartPrice = document.getElementsByClassName("CartPrice")[0];
    let ExplanationTextarea = document.getElementsByClassName("ExplanationTextarea")[0].value;
    let CartIndivoduce = document.getElementsByClassName("CartIndivoduce")[0];
    if (CartNameValue.length > 0) {
        CartName.textContent = CartNameValue;
    }
    if (TypeSelect.value.length > 0) {
        CartType.textContent = "商品分類 : " + TypeSelect.value;
    }
    if (TypeSelect.value === "請選擇") {
        CartType.textContent = "商品分類 : ";
    }
    CartPrice.textContent = "$" + SellingPrice;

    if (ExplanationTextarea.length > 0) {
        CartIndivoduce.textContent = "商品說明 : " + ExplanationTextarea
    }
    if (ExplanationTextarea.length === 0) {
        CartIndivoduce.textContent = "商品說明 : "
    }
}
