import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/Machine/Machine.scss";
function MarketingReview({
  buttonTitle,
  buttonClassName,
  modalTitle,
  enterColor,
  closeColor,
  removeColor,
  activities,
}) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  //刪除單筆餐廳屬性

  //控制狀態
  // const stateHandler = () => {};

  return (
    <>
      <Button variant={buttonClassName} onClick={handleShow}>
        {buttonTitle}
      </Button>

      <Modal
        size="ml"
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h4 style={{ fontWeight: "bold" }}>{modalTitle}</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            {activities.map((item) => {
              return (
                <div className="d-flex border-bottom py-3" key={item.index}>
                  <div className="w-25 ">{item.title}</div>
                  <div>{item.content}</div>
                </div>
              );
            })}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <div className="d-flex w-100">
            <div className="me-auto">
              <Button
                variant="btn btn-danger text-white "
                onClick={handleClose}
              >
                審核不通過
              </Button>
            </div>
            <div className="me-3">
              <Button variant={enterColor}>審核通過</Button>
            </div>
            <div>
              <Button variant={removeColor} onClick={handleClose}>
                返回
              </Button>
            </div>
          </div>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default MarketingReview;
