//套件範例網址 https://react-switch.netlify.app/

import React from "react";
import Switch from "react-switch";
// import { CloseOutlined, CheckOutlined } from '@ant-design/icons';
import "../../styles/ReactElement/switchButton.css";
function SwitchButton({ companyData, setCompanyData, switchRef, handleInput }) {
  // const [checked, setChecked] = useState(false);
  const handleChange = (storeState) => {
    console.log(storeState);
    console.log(switchRef.current.props["data-order"]);
    setCompanyData({ storeState: storeState });
  };

  return (
    <div>
      <div>
        {/* <h6>狀態</h6> */}
        <label className="switchBox">
          <Switch
            ref={switchRef}
            data-order="storeState"
            // border={true}
            offColor="#FFF"
            onColor="#00BFA5"
            offHandleColor="#FFF"
            onHandleColor="#FFF"
            height={26}
            width={61}
            borderRadius={3}
            handleDiameter={16}
            // onClick={(even) => {
            //   handleClick(even);
            // }}
            onChange={(e) => {
              handleChange(e);
              // handleInput(e);
            }}
            checked={companyData}
            boxShadow="0px 0px 2px rgba(0, 0, 0, 0.6)"
            uncheckedIcon={
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  height: "100%",
                  fontSize: 15,
                  color: "#9DA5B1",
                  paddingRight: 2,
                }}
              >
                關閉
              </div>
            }
            checkedIcon={
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  height: "100%",
                  fontSize: 15,
                  color: "#FFF",
                  paddingRight: 2,
                }}
              >
                開啟
              </div>
            }
          />
        </label>
        {/* <p>
          The switch is <span>{checked ? "on" : "off"}</span>.
        </p> */}
      </div>
    </div>
  );
}

export default SwitchButton;
