import React, { useEffect } from "react";
import penIcon from "../../../images/svg/ui-icon/cil-pencil.svg"
import topArrew from "../../../images/svg/ui-icon/cil-arrow-top.svg"
import * as PutData from "../../../components/Json/PutData.json"


// =====餐點結單標題======
export function LabelTitle(props) {
    useEffect(() => {
        setTimeout(() => {
            UptownContent();
        }, 100);
    });
    let LabelTitleFake = [];
    let LabelTitleTwin = [];
        for (let x = 0; x < parseInt(localStorage.LabelTitleCost); x++) {
            LabelTitleFake.push(
                <th
                    key={x}
                    className={"ChildTitleFake ChildTitleFake" + x}
                    style={{
                        color: "#2F353A",
                        padding: "5px 0",
                        fontSize: "14px",
                        fontWeight: "bolder",
                        backgroundColor: "#E7EBEF",
                        border: "none",
                        lineHeight: "40px"
                    }}>
                    {props.TitleText}
                    <img alt=""
                        className="thisPenIcon"
                        src={penIcon}></img>
                </th>
            )
            LabelTitleTwin.push(
                <th
                    key={x}
                    className={"ChildTitle" + x}
                    style={{
                        color: "#2F353A",
                        height: "40px",
                        fontSize: "14px",
                        fontWeight: "bolder",
                        backgroundColor: "#E7EBEF",
                        border: "1px solid #C8CED3"
                    }}>
                    <span
                        className="LabelTitleText"></span><img
                            className={"arrangementTop"}
                            alt=""
                            onClick={() => {
                                arrangementChange(x);
                            }}
                            style={{
                                with: "3px",
                                height: "10px",
                                marginTop: "6px",
                                marginRight: "3px",
                                cursor: "pointer",
                                float: "right"
                            }}
                            src={topArrew}></img>
                </th>
            )
        }
    return <thead>
        <tr>
            {LabelTitleFake}
        </tr>
        <tr>
            {LabelTitleTwin}
        </tr>
    </thead>
}

// =============表單上方CSS調整===============
export function UptownContent() {
    let ChildTitleFake = document.getElementsByClassName("ChildTitleFake");
    let thisPenIcon = document.getElementsByClassName("thisPenIcon");
    
    for (let index = 0; index < ChildTitleFake.length; index++) {
        if (index % parseInt(localStorage.LabelTitleCost) === 0) {
            ChildTitleFake[index].style.color = "#2F353A";
            thisPenIcon[index].style.display = "initial";
            ChildTitleFake[index].style.paddingLeft = "10px"
        }
        else {
            ChildTitleFake[index].style.color = "#E7EBEF";
        }
    }
}

let isTop = true;
// 表格標題旁的排序箭頭效果
function arrangementChange(arrangement) {
    let arrangementTop = document.getElementsByClassName("arrangementTop");
    isTop = !isTop
    for (let x = 0; x < arrangementTop.length; x++) {
        arrangementTop[x].style.transform = "rotate(" + 0 + "deg)";
    }
    arrangementTop[arrangement].style.transform = isTop ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
}

let DataArray = [];
; (function () {
    for (let i = 0; i < PutData.DataCompent.length; i++) {
        DataArray.push(PutData.DataCompent[i]["DataArray"]);
    }
})();

// ==============資料多寡製造出表格欄位================
export function LabelCheckerboard() {
    let LabelDatasTr = [];
    let ChildTd = [];
    let LabelDatasTd = [];
    // 表格標題(包含點擊+圖片)
    for (let x = 0; x < localStorage.LabelTitleCost; x++) {
        LabelDatasTd.push(<td key={x} className={"ChildTd ChildTd" + x}>
            <span className="clickMeSpan"></span>
            <span className="clickMeText"></span>
        </td>)
    }
    // 手機螢幕所需要的表格
    for (let y = 3; y < localStorage.LabelTitleCost; y++) {
        ChildTd.push(<div key={y}>
            <span className={"ChildGroupTitle ChildGroupTitle" + y}></span>
            <span className={"ChildGroupContent ChildGroupContent" + y}></span>
        </div>)
    }
    // 表格欄位
    for (let z = 0; z < localStorage.LabelTitleCost; z++) {
        LabelDatasTr.push(<tr key={z} className="keyMan">{LabelDatasTd}</tr>)
        LabelDatasTr.push(<tr key={z + "tr"} className="ChildGroup">
            <td>{ChildTd}</td>
        </tr>
        )
    }
    return <tbody>
        {LabelDatasTr}
    </tbody>
}

export function MealStatementLabel() {
    let ChienLiLabel = [];
    for (let x = 0; x < PutData.DataCompent.length; x++) {
        ChienLiLabel.push(<table key={x}
            style={{
                marginTop: "20px"
            }}
            className="ChienLiLabel">
            <LabelTitle TitleText={PutData.DataCompent[x]["Building"]} />
            <LabelCheckerboard />
            <thead>
                <tr >
                    <th>
                        <span className="RemainCost">空櫃剩餘數量：</span>
                        <span className="RemainNum"></span>
                    </th>
                </tr>
            </thead>
        </table>
        )
    }
    return <>
        {ChienLiLabel}
    </>
}

export function MealTimeLabel() {
    let ChienLiLabel = [];
    for (let x = 0; x < PutData.DataCompent.length; x++) {
        ChienLiLabel.push(<table key={x}
            style={{
                marginTop: "20px"
            }}
            className="ChienLiLabel">
            <LabelTitle TitleText={PutData.DataCompent[x]["Building"]} />
            <LabelCheckerboard />
        </table>
        )
    }
    return <>
        {ChienLiLabel}
    </>
}
