//店家資料裡面的INPUT格式
import React from "react";

function StoreCustomInput({
  title,
  inputName,
  handelChange,
  companyData,
  companyDataError,
  handleInput,
  order,
  required,
  maxlength,
}) {
  return (
    <div className="storeCreatTextItem col-12 col-xl-4 ">
      <h6>
        {title}
        {required && <span style={{ color: "red" }}>*</span>}
      </h6>
      <input
        data-order={order}
        type="text"
        placeholder={companyData[inputName] === "" ? `請輸入${title}` : ""}
        name={inputName}
        onChange={handelChange}
        required
        value={companyData[inputName]}
        maxLength={maxlength || ""}
        style={{
          borderColor: companyDataError[inputName] ? "red" : "#E4E7EA",
        }}
      />
      <small style={{ color: "red" }}>
        {companyDataError && companyDataError[inputName]}
      </small>
    </div>
  );
}

export default StoreCustomInput;
