import { LoginApi, ChangeApi, ForgotApi } from "./getApi"
let emailRule =
  /^\w+((-\w+)|(\.\w+))*\@[A-Za-z0-9]+((\.|-)[A-Za-z0-9]+)*\.[A-Za-z]+$/;

// =========登入確認帳號密碼=================
export function CheckPW() {
    let mailVaule = document.getElementsByClassName("form-control")[0].value;
    let passwordVaule = document.getElementsByClassName("form-control")[1].value;
    let AlertText = document.getElementsByClassName("AlertText")[0];
    AlertText.style.display = "none";
    if (mailVaule.length === 0 && passwordVaule.length === 0) {
      console.log("不用發生任何事件 吧......");
    } else if (mailVaule.length > 0 && passwordVaule.length === 0) {
      AlertText.style.display = "block";
      AlertText.innerHTML = "請輸入密碼";
    } else if (mailVaule.length > 0 && passwordVaule.length > 0) {
      if (mailVaule.search(emailRule) === -1) {
        AlertText.style.display = "block";
        AlertText.innerHTML = "帳號格式錯誤";
      } else {
        AlertText.style.display = "none";
        setTimeout(() => {
          LoginApi(mailVaule, passwordVaule);
        }, 100);
      }
    }
  }
  // ========變更密碼=================
  export function ChangePW() {
    let mailVaule = document.getElementsByClassName("form-control")[0].value;
    let passwordVaule = document.getElementsByClassName("form-control")[1].value;
    let checkPasswordVaule =
      document.getElementsByClassName("form-control")[2].value;
    let AlertText = document.getElementsByClassName("AlertText")[0];
    if (mailVaule.length === 0 && passwordVaule.length === 0) {
      console.log("不用發生任何事件 吧......");
    } else {
      if (mailVaule.search(emailRule) === -1) {
        AlertText.style.display = "block";
        AlertText.innerHTML = "帳號格式錯誤";
      } else if (
        mailVaule.search(emailRule) === 0 &&
        passwordVaule.length === 0
      ) {
        AlertText.style.display = "block";
        AlertText.innerHTML = "請輸入密碼";
      } else if (
        mailVaule.search(emailRule) === 0 &&
        passwordVaule === checkPasswordVaule
      ) {
        ChangeApi(mailVaule, passwordVaule, checkPasswordVaule);
      } else if (
        mailVaule.search(emailRule) === 0 &&
        passwordVaule !== checkPasswordVaule
      ) {
        AlertText.style.display = "block";
        AlertText.innerHTML = "請輸入相同密碼";
      }
    }
  }
  // ====================忘了密碼 請寄信=================
  export function DontForgot() {
    let mailVaule = document.getElementsByClassName("form-control")[0].value;
    if (mailVaule.search(emailRule) === 0) {
      ForgotApi(mailVaule);
    }
  }
