let timeStart, timeEnd, time;
export function getTimeNow()//獲取此刻時間
{
    var now = new Date();
    return now.getTime();
}

export function passwordShow()//滑鼠按下時觸發
{
    let password = document.getElementsByClassName("whoU");
    timeStart = getTimeNow();
    time = setInterval(function () {
        timeEnd = getTimeNow();
        if (timeEnd - timeStart > 300) {
            clearInterval(time);
            if (password[1].value > 0) {
                password[1].type = "text";
            }
            if (password.length === 3) {
                if (password[2].value > 0) {
                    password[2].type = "text";
                }
            }
        }
    }, 100);
}
export function passwordHide() {
    clearInterval(time);//如果按下時間不到300毫秒便彈起，
    let password = document.getElementsByClassName("whoU");
    if (password[1].value > 0) {
        password[1].type = "password";
    }
    if (password.length === 3) {
        if (password[2].value > 0) {
            password[2].type = "password";
        }
    }
}
