import React from "react";
import { useHistory } from "react-router-dom";
import { DateInputGroup } from "../../../components/ReactElement/InputCombo";
import MarketingReview from "../../../components/Marketing/MarketingReview";
import {
  ComponentTable,
  ComponentPage,
} from "../../../components/Machine/ComponentTable";
import "../../../styles/Marketing/marketing.scss";
const activities = [
  { index: "1", title: "行銷活動", content: "單品折扣" },
  { index: "2", title: "適用店家", content: "水果新店店、景美店" },
  { index: "3", title: "活動名稱", content: "EX:指定商品折價10元" },
  { index: "4", title: "活動詳情", content: "買指定商品折價10元" },
  { index: "5", title: "日期起訖", content: "2020/02/20-2020/02/30" },
  { index: "6", title: "折價類別", content: "水果類" },
  { index: "7", title: "折扣", content: "固定折價:10元" },
  { index: "8", title: "總預算", content: "2,000,000元" },
];
const data = [
  {
    Index: 1,
    Activity: "單品折扣",
    Name: "麵類品項全面折50",
    DateStart: "2020/02/20~2021/0220",
    States: "啟用",
  },
  {
    Index: 2,
    Activity: "單品折扣",
    Name: "麵類品項全面折50",
    DateStart: "2020/02/20~2021/0220",
    States: "啟用",
  },
];
function Marketing() {
  const history = useHistory();
  return (
    <div className="">
      <DateInputGroup />
      <button
        className="btn btn-success text-white"
        onClick={() => {
          history.push("/marketing/creat");
        }}
      >
        新增
      </button>
      <div className="container">
        <ComponentTable
          dataHeard={[
            "排序",
            "行銷活動",
            "活動名稱",
            "日期起訖",
            "狀態",
            "操作",
          ]}
          data={data}
          dataOrder={[
            "Index",
            "Activity",
            "Name",
            "DateStart",
            "States",
            "Button",
          ]}
          operate={
            <>
              <MarketingReview
                buttonTitle={"活動審核"}
                buttonClassName={"btn btn-warning warning-text m-1"}
                modalTitle={"行銷活動審核"}
                enterColor={"btn btn-success text-white"}
                removeColor={"btn btn-light"}
                activities={activities}
              />
              <button className="btn btn-primary text-white m-1">編輯</button>
              <button className="btn btn-danger text-white m-1">刪除</button>
            </>
          }
        />
        <ComponentPage />
      </div>
    </div>
  );
}

export default Marketing;
