// 使用者登入效果
export function ImInside() {
    let UserInside = document.getElementsByClassName("cmsHeaderIcon")[1];
    let UserInsideIcon = document.getElementsByClassName("cmsHeaderImg")[1];
    let LoginName = document.getElementsByClassName("LoginName")[0];
    if (UserInside !== undefined) {
        UserInside.style.width = "100px";
        UserInsideIcon.style.width = "19px";
        LoginName.style.display = "block";
    }
}

// 使用者登出效果
export function ImOut() {
    let UserInside = document.getElementsByClassName("cmsHeaderIcon")[1];
    let LoginName = document.getElementsByClassName("LoginName")[0];
    if (UserInside !== undefined) {
        UserInside.style.width = "37px";
        LoginName.style.display = "none";
    }
}
