import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/ReplenishmentManagement/replenishmentOrder.scss";
function MachineErrorReturn() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="btn btn-success text-white" onClick={handleShow}>
        確認回報
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>機台商品-異常回報</Modal.Title>
        </Modal.Header>
        <Modal.Body className="replenishmenOrderBody">
          <select name="" id="">
            <option value="">我壞了</option>
            <option value="">我累了</option>
            <option value="">我不行了</option>
            <option value="">快救救我</option>
          </select>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="light" onClick={handleClose}>
            返回
          </Button>
          <Button variant="success text-white" onClick={handleClose}>
            確認
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default MachineErrorReturn;
