import React, { useState } from "react";
import ReactBsModal from "react-bootstrap/Modal";
import "../../styles/AlertModal/courtAlert.css"
import "../../styles/AlertModal/ManagemenAlert.css"
import { ColorBtn } from "./ColorBtn"
import { LongColorBtn } from "./ColorBtn"


//============= 餐點審核按鈕 ===============
export function AssessBtn(props) {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <>
            <button style={{
                height: "35px",
                width: "80px",
                lineHeight: "32px",
                borderRadius: "4px",
                fontSize: "14px",
                backgroundColor: props.BtnBackgroundColor,
                color: props.BtnColor,
                cursor: "pointer",
                border: "none",
            }
            } variant="primary" onClick={handleShow}>
                {props.BtnTitle}
            </button>
            <ReactBsModal show={show} onHide={handleClose}>
                <ReactBsModal.Header closeButton>
                    <ReactBsModal.Title className="ModalTitle">餐點審核</ReactBsModal.Title>
                </ReactBsModal.Header>
                <div className="productType">商品分類: {props.type}</div>
                <div className="productText">{props.text}</div>
                <div className="productIndivoduce">{props.indivoduce}
                    <img alt="" src={props.pic}></img></div>
                <div className="productPrice">{props.Amount}</div>
                <div className="whynot">審核不通過原因</div>
                <textarea className="whynotTextarea" placeholder="請輸入原因"></textarea>
                <hr />
                <footer style={{
                    display: "flex",
                    paddingBottom: "20px"
                }}>
                    <LongColorBtn className="NoPassBtn" text="審核不通過" color="white" backgroundColor="#F85756"
                        onClick={() => {
                            alert("不通過")
                        }} />
                    <div className="backBtns">
                        <LongColorBtn className="PassBtn" text="審核通過" color="white" backgroundColor="#00BFA5"
                            onClick={() => {
                                alert("通過")
                            }} />
                        <ColorBtn className="BackBtn" text="返回" backgroundColor="#C8CED3" variant="primary" onClick={handleClose} />
                    </div>
                </footer>
            </ReactBsModal>
        </>)
}
