import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/Machine/Machine.scss";
function MachineEven({
  buttonTitle,
  buttonClassName,
  modalTitle,
  enterColor,
  closeColor,
  removeColor,
}) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  //刪除單筆餐廳屬性

  //控制狀態
  // const stateHandler = () => {};

  return (
    <>
      <Button variant={buttonClassName} onClick={handleShow}>
        {buttonTitle}
      </Button>

      <Modal
        // size="lg"
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h4 style={{ fontWeight: "bold" }}>{modalTitle}</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="">
            <table className="table table-bordered text-center mb-3">
              <tbody>
                <tr style={{ backgroundColor: "#e6ebef" }}>
                  <th scope="col">ID</th>
                  <th scope="col">機器辨別碼</th>
                  <th scope="col">事件代碼</th>
                  <th scope="col">事件簡述</th>
                  <th scope="col">事件時間</th>
                </tr>
                <tr>
                  <td>140073</td>
                  <td>HE0000WTW001</td>
                  <td>9527</td>
                  <td>9527您的中生代馬</td>
                  <td>2020/02/22 02:22:22</td>
                </tr>
              </tbody>
            </table>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant={removeColor} onClick={handleClose}>
            返回
          </Button>
          <Button variant={enterColor}>確認</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default MachineEven;
