import React, { useEffect } from "react";
import penIcon from "../../../images/svg/ui-icon/cil-pencil.svg"
import topArrew from "../../../images/svg/ui-icon/cil-arrow-top.svg"
import * as PutData from "../../../components/Json/PutData.json"

// =====餐點結單標題======
export function MealStatementTitle(props) {
    useEffect(() => {
        setTimeout(() => {
            UptownContent();
        }, 100);
    });
    let LabelTitleFake = [];
    let LabelTitleTwin = [];
    for (let x = 0; x < parseInt(localStorage.LabelTitleCost); x++) {
        LabelTitleFake.push(
            <th
                key={x}
                className={"ChildTitleFake ChildTitleFake" + x}
                style={{
                    color: "#2F353A",
                    padding: "5px 0",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "none",
                    lineHeight: "40px"
                }}>
                {props.TitleText}
                <img alt=""
                    className="thisPenIcon"
                    src={penIcon}></img>
            </th>
        )
        LabelTitleTwin.push(
            <th
                key={x}
                className={"ChildTitle" + x}
                style={{
                    color: "#2F353A",
                    height: "40px",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3"
                }}>
                <span
                    className="LabelTitleText"></span><img
                        className={"arrangementTop"}
                        alt=""
                        onClick={() => {
                            arrangementChange(x);
                        }}
                        style={{
                            with: "3px",
                            height: "10px",
                            marginTop: "6px",
                            marginRight: "3px",
                            cursor: "pointer",
                            float: "right"
                        }}
                        src={topArrew}></img>
            </th>
        )
    }
    return <thead>
        <tr>
            {LabelTitleFake}
        </tr>
        <tr>
            {LabelTitleTwin}
        </tr>
    </thead>
}

// =====餐點時段設定標題======
export function MealTimeTitle(props) {
    useEffect(() => {
        setTimeout(() => {
            UptownContent();
        }, 100);
    });
    let LabelTitleFake = [];
    let LabelTitleTwin = [];
    for (let x = 0; x < parseInt(localStorage.LabelTitleCost2); x++) {
        LabelTitleFake.push(
            <th
                key={x}
                className={"ChildTitleFake ChildTitleFake" + x}
                style={{
                    color: "#2F353A",
                    padding: "5px 0",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "none",
                    lineHeight: "40px"
                }}>
                {props.TitleText}
                <img alt=""
                    className="thisPenIcon"
                    src={penIcon}></img>
            </th>
        )
        LabelTitleTwin.push(
            <th
                key={x}
                className={"ChildTitle2" + x}
                style={{
                    color: "#2F353A",
                    height: "40px",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3"
                }}>
                <span
                    className="LabelTitleText2"></span><img
                        className={"arrangementTop"}
                        alt=""
                        onClick={() => {
                            arrangementChange(x);
                        }}
                        style={{
                            with: "3px",
                            height: "10px",
                            marginTop: "6px",
                            marginRight: "3px",
                            cursor: "pointer",
                            float: "right"
                        }}
                        src={topArrew}></img>
            </th>
        )
    }
    return <thead>
        <tr>
            {LabelTitleFake}
        </tr>
        <tr>
            {LabelTitleTwin}
        </tr>
    </thead>
}

export function UptownContent() {
    let ChildTitleFake = document.getElementsByClassName("ChildTitleFake");
    let thisPenIcon = document.getElementsByClassName("thisPenIcon");
    let LabelTitleText = document.getElementsByClassName("LabelTitleText");
    for (let index = 0; index < ChildTitleFake.length; index++) {
        if (index % parseInt(localStorage.LabelTitleCost) === 0) {
            ChildTitleFake[index].style.color = "#2F353A";
            thisPenIcon[index].style.display = "initial";
            ChildTitleFake[index].style.paddingLeft = "10px"
        }
        else {
            ChildTitleFake[index].style.color = "#E7EBEF";
        }
    }

    // 表單標題文字引入
    for (let x = 0; x < LabelTitleText.length; x++) {
        if (x % parseInt(localStorage.LabelTitleCost) === 0) {
            LabelTitleText[x].style.marginLeft = "5px";
            LabelTitleText[x].innerHTML = "店家編號";
        }
        if (x % parseInt(localStorage.LabelTitleCost) === 1) {
            LabelTitleText[x].innerHTML = "店家";
        }
        if (x % parseInt(localStorage.LabelTitleCost) === 2) {
            LabelTitleText[x].innerHTML = "商品編號";
        }
        if (x % parseInt(localStorage.LabelTitleCost) === 3) {
            LabelTitleText[x].innerHTML = "商品名稱";
        }
        if (x % parseInt(localStorage.LabelTitleCost) === 4) {
            LabelTitleText[x].innerHTML = "預定";
        }
        if (x % parseInt(localStorage.LabelTitleCost) === 5) {
            LabelTitleText[x].innerHTML = "現場";
        }
        if (x % parseInt(localStorage.LabelTitleCost) === 6) {
            LabelTitleText[x].innerHTML = "操作";
        }
    }
}

let isTop = true;
// 表格標題旁的排序箭頭效果
function arrangementChange(arrangement) {
    let arrangementTop = document.getElementsByClassName("arrangementTop");
    isTop = !isTop
    for (let x = 0; x < arrangementTop.length; x++) {
        arrangementTop[x].style.transform = "rotate(" + 0 + "deg)";
    }
    arrangementTop[arrangement].style.transform = isTop ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
}

let DataArray = [];
; (function () {
    for (let i = 0; i < PutData.DataCompent.length; i++) {
        DataArray.push(PutData.DataCompent[i]["DataArray"]);
    }
})();

// ==============餐點結單資料多寡製造出表格欄位================
export function MealStatementCheckerboard() {
    let LabelDatasTr = [];
    let ChildTd = [];
    let LabelDatasTd = [];
    // 表格標題(包含點擊+圖片)
    for (let x = 0; x < 7; x++) {
        LabelDatasTd.push(<td key={x} className={"ChildTd ChildTd" + x}>
            <span className="clickMeSpan"></span>
            <span className="clickMeText"></span>
        </td>)
    }
    // 手機螢幕所需要的表格
    for (let y = 3; y < 7; y++) {
        ChildTd.push(<div key={y}>
            <span className={"ChildGroupTitle ChildGroupTitle" + y}></span>
            <span className={"ChildGroupContent ChildGroupContent" + y}></span>
        </div>)
    }
    // 表格欄位
    for (let z = 0; z < 2; z++) {
        LabelDatasTr.push(<tr key={z} className="keyMan">{LabelDatasTd}</tr>)
        LabelDatasTr.push(<tr key={z + "tr"} className="ChildGroup">
            <td>{ChildTd}</td>
        </tr>
        )
    }
    return <tbody>
        {LabelDatasTr}
    </tbody>
}

