import * as Exampledata from "../../../components/Json/ExampleLabel.json"
import ReactDOM from 'react-dom';
// 引入按鈕
import { ClickMe, TwinBtns, ChildTwinBtns } from "../../../components/ReactElement/ColorBtn";
// 引入函式
import { UnfoldTd } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function ChangeWorld() {
  // ================監控區==================
  let DataTitle = ["排序", "訂單編號", "銷售機器", "訂購類型", "金額", "付款方式", "操作"];
  let context = document.getElementsByClassName("context")[0];
  let LabelTitleText = document.getElementsByClassName("LabelTitleText");
  let clickMeSpan = document.getElementsByClassName("clickMeSpan");
  let clickMeText = document.getElementsByClassName("clickMeText");
  let ChildTitle0 = document.getElementsByClassName("ChildTitle0")[0];
  let ChildTd1 = document.getElementsByClassName("ChildTd1");
  let ChildTd2 = document.getElementsByClassName("ChildTd2");
  let ChildTd3 = document.getElementsByClassName("ChildTd3");
  let ChildTd4 = document.getElementsByClassName("ChildTd4");
  let ChildTd5 = document.getElementsByClassName("ChildTd5");
  let ChildTd6 = document.getElementsByClassName("ChildTd6");
  let ChildTitle6 = document.getElementsByClassName("ChildTitle6");
  let ChildGroupTitle3 = document.getElementsByClassName("ChildGroupTitle3");
  let ChildGroupContent3 = document.getElementsByClassName("ChildGroupContent3");
  let ChildGroupTitle4 = document.getElementsByClassName("ChildGroupTitle4");
  let ChildGroupContent4 = document.getElementsByClassName("ChildGroupContent4");
  let ChildGroupTitle5 = document.getElementsByClassName("ChildGroupTitle5");
  let ChildGroupContent5 = document.getElementsByClassName("ChildGroupContent5");
  let ChildGroupTitle6 = document.getElementsByClassName("ChildGroupTitle6");
  let ChildGroupContent6 = document.getElementsByClassName("ChildGroupContent6");

  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "label" ||
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "store") {
    // ================Label頁面微調CSS=======================
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    context.style.paddingBottom = "50px";
    ChildTitle0.style.width = "50px";
    ChildTitle0.style.textAlign = "center";
    ChildTitle0.style.padding = "3px 0";
    ChildTitle6[0].style.width = "140px";
  }
  for (let x = 0; x < ChildTd6.length; x++) {
    ChildTd6[x].style.paddingLeft = "0px";
  }
  if (window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Sorting") {
    let paginationThree = document.getElementsByClassName("paginationThree")[0];
    paginationThree.style.display = "block";
    let paginationGroup = document.getElementsByClassName("paginationGroup");
    paginationGroup[1].style.height = "31px";
    paginationGroup[2].style.height = "31px";
  }
  // 表單標題文字引入
  for (let x = 0; x < DataTitle.length; x++) {
    LabelTitleText[x].innerHTML = DataTitle[x];
  }
  //表單欄位內容引入
  for (let i = 0; i < localStorage.MaxPageValue; i++) {
    clickMeText[i].innerHTML = Exampledata.DataCompent[i]["Index"];
    ChildTd1[i].innerHTML = Exampledata.DataCompent[i]["Number"];
    ChildTd2[i].innerHTML = Exampledata.DataCompent[i]["Machine"];
    ChildTd3[i].innerHTML = Exampledata.DataCompent[i]["Buyer"];
    ChildTd4[i].innerHTML = Exampledata.DataCompent[i]["Amount"];
    ChildTd5[i].innerHTML = Exampledata.DataCompent[i]["Type"];
    ChildGroupTitle3[i].innerHTML = DataTitle[3];
    ChildGroupContent3[i].innerHTML = Exampledata.DataCompent[i]["Buyer"];
    ChildGroupTitle4[i].innerHTML = DataTitle[4];
    ChildGroupContent4[i].innerHTML = Exampledata.DataCompent[i]["Amount"];
    ChildGroupTitle5[i].innerHTML = DataTitle[5];
    ChildGroupContent5[i].innerHTML = Exampledata.DataCompent[i]["Type"];
    ChildGroupTitle6[i].innerHTML = DataTitle[6];
    ReactDOM.render(<ChildTwinBtns />, ChildGroupContent6[i]);
    ReactDOM.render(<ClickMe onClick={() => { UnfoldTd(i) }} />, clickMeSpan[i]);
    ReactDOM.render(<TwinBtns />, ChildTd6[i]);
  }
}
