//店家管理左側列表
import React, { useState } from "react";
import EditRestaurant from "../ReactElement/EditRestaurant";
import { Link } from "react-router-dom";
function StoreList({ storeList, storeClickId, setStoreClickId }) {
  const [select, setSelect] = useState(null);
  const [companyList] = useState([
    {
      id: "1",
      company: "快樂肥胖公司",
      store: [
        { name: "肥宅快樂水分店" },
        { name: "胖寶王" },
        { name: "吃不飽" },
      ],
    },
    {
      id: "2",
      company: "168包你瘦公司",
      store: [
        { name: "萬華公園店" },
        { name: "就怕你不來店" },
        { name: "客人您好盤分店" },
      ],
    },
    {
      id: 3,
      company: "真有錢投資公司",
      store: [{ name: "水溝分店" }, { name: "真心不片分店" }],
    },
  ]);
  return (
    <div>
      <div className="storeLeft">
        <div className="storeTitle">
          <h6>公司/店家 結構</h6>
          {/* <imgCropper /> */}
        </div>
        <div className="storeCreat">
          {/* 測試陣列 */}
          {companyList.map((v) => {
            return (
              <div className="storeList" key={v.id}>
                <ul>
                  <input
                    type="button"
                    name="companyClick"
                    id={`companyClick${v.id}`}
                    className="storeListBtn"
                    value={select === v.id ? "-" : "+"}
                    onClick={() => {
                      select === v.id ? setSelect(null) : setSelect(v.id);
                    }}
                  />

                  <label>{v.company}</label>
                  {select === v.id &&
                    v.store.map((item, index) => {
                      return (
                        <div className="d-flex" key={index}>
                          <div className="storeListBox"></div>
                          <li>{item.name}</li>
                        </div>
                      );
                    })}
                </ul>
              </div>
            );
          })}
        </div>
        待完整公司含店家API 以下純店家API串接
        <ul className="d-flex flex-column">
          {storeList?.map((item) => {
            return (
              <button
                key={item.id}
                onClick={() => {
                  setStoreClickId(item.id);
                }}
              >
                <li>{item.name}</li>
              </button>
            );
          })}
        </ul>
        {/* <div className="storeCreat">
          <p>店家:店家甲資料</p>
          <div className="cmsSideIcon">
            <img src={cilPencil} alt="" />
          </div>
          <div className="cmsSideIcon" style={{ backgroundColor: "#F85756" }}>
            <img src={cilTrash} alt="" />
          </div>
        </div> */}
        <div className="storeCreat ">
          <Link to="/storecreat">
            <button className="storeCreatText">新增公司/店家</button>
          </Link>
        </div>
        <div className=" d-flex justify-content-center  py-3 px-1">
          <EditRestaurant
            buttonTitle={"管理餐廳屬性"}
            buttonClassName={"btn btn-outline-dark w-100 mx-2"}
            modalTitle={"編輯餐廳屬性"}
            removeColor={"light"}
            enterColor={"success text-white"}
          />
        </div>
      </div>
    </div>
  );
}

export default StoreList;
