import React from "react";
import Swal from "sweetalert2";
function StoreUserDataInfo({
  handelChange,
  companyData,
  storeUeserStoreData,
  setUserData,
}) {
  return (
    <>
      <div className="storeCreatGroup">
        <div className="storeCreatTextTitle">
          <p>新增使用者資料</p>
        </div>
        <div className="storeCreatTextBody">
          <div className="storeCreatTextItem">
            <h6>帳號</h6>
            <input
              type="text"
              placeholder="請輸入"
              name="storeUeserAccount"
              onChange={handelChange}
              required
              value={companyData.storeUeserAccount}
            />
          </div>
          <div className="storeCreatTextItem">
            <h6>使用者姓名</h6>
            <input
              type="text"
              placeholder="請輸入"
              name="storeUeserName"
              onChange={handelChange}
              required
              value={companyData.storeUeserName}
            />
          </div>
          <div className="storeCreatTextItem" style={{ width: "85%" }}>
            <h6>職位權限</h6>
            <input
              type="text"
              placeholder="請輸入"
              name="storeUeserAuthority"
              onChange={handelChange}
              required
              value={companyData.storeUeserAuthority}
            />
          </div>
          <div className="storeCreatTextItem storeCreatTextItemCheck">
            <h6>可用店家權限</h6>
            <div className="d-flex">
              {storeUeserStoreData.map((item, index) => (
                <div
                  className="storeCreatTextItemCheckGroup "
                  key={index}
                  style={{ lineHeight: "1px", alignItems: "center" }}
                >
                  <input
                    type="checkbox"
                    value={item}
                    name="storeUeserStore"
                    onChange={handelChange}
                    style={{
                      width: "auto",
                    }}
                  />
                  <label>{item}</label>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="storeCreatButton">
          <button
            onClick={() => {
              setUserData("2");
            }}
          >
            <h6>上一步</h6>
          </button>
          <button
            onClick={() => {
              Swal.fire(
                "資料建立完成",
                "系統將發送邀請信至使用者信箱!",
                "success"
              );
            }}
          >
            <h6>完成</h6>
          </button>
        </div>
      </div>
    </>
  );
}

export default StoreUserDataInfo;
