import React, { useState } from "react";
import ReactBsModal from "react-bootstrap/Modal";
import { LongLabelInput, LabelSelect } from "../InputCombo"
import { UploadInput } from "../DataUpladBtn"


//================編輯銀行資料視窗效果====================
export function EditBankBtn(props) {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <>
            <button
                className={props.className}
                variant="primary" onClick={() => {
                    handleShow();
                    setTimeout(() => {
                        let How = document.getElementsByClassName("modal-content");
                        if (
                            window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "Finance") {
                            How[0].className = "HowModal-content";
                        }
                    })
                }}>
                {props.BtnTitle}
            </button>
            <ReactBsModal show={show} onHide={handleClose}>
                <ReactBsModal.Header closeButton>
                    <ReactBsModal.Title className="ModalTitle">編輯銀行資料</ReactBsModal.Title>
                </ReactBsModal.Header>
                <div className="EditBankBtn">
                    <div className="BankDatas">
                        <LongLabelInput p="銀行名稱" placeholder="請輸入名稱" />
                        <div style={{
                            display: "flex",
                            marginLeft: "-2px",
                            marginTop: "30px",
                            width: "20%"
                        }}>
                            <LabelSelect /><LabelSelect />
                        </div>
                        <LongLabelInput p="銀行帳號" placeholder="請輸入銀行帳號" />
                        <div style={{ height: "10px" }}></div>
                        <LongLabelInput p="銀行戶名" placeholder="請輸入銀行戶名" />
                    </div>

                    <div className="PassbookPic">
                        <p className="PassbookTitle">銀行存摺</p>
                        <UploadInput className="EditBtnUp" specification="檔案格式:JPG.PNG" />
                        <div className="ScreenPassbook"></div>
                    </div>
                </div>
            </ReactBsModal>
        </>)
}
