//使用者群組列表
import React, { useState } from "react";
import "../../../styles/usergroups/usergroups.scss";
import EditRestaurant from "../../../components/userGroup/EditRestaurant";
function UserGroups() {
  const [deleteInput, setDeleteInput] = useState(false);
  const [editInput, setEditInput] = useState(false);

  const deleteHandle = () => {
    setDeleteInput(!deleteInput);
  };
  const editHandle = () => {
    setEditInput(!editInput);
  };
  return (
    <div className="storeContext">
      <div className="storeGroup">
        <div className="storeLeft">
          <div className="storeTitle">
            <h6>職位權限</h6>
          </div>
          <div className="storeCreat py-2 ">
            <p>甘蔗老董</p>
          </div>
          <div className="storeCreat  py-2">
            <p>煞氣a小屁孩</p>
          </div>

          <div className="storeCreat text-center py-2">
            <EditRestaurant
              buttonTitle={"新增職位權限"}
              buttonClassName={"btn btn-outline-success"}
              modalTitle={"新增職位權限"}
              enterColor={"btn btn-success text-white"}
              removeColor={"btn btn-light"}
            />
          </div>
          <div></div>
        </div>
        <div className="storeRight">
          <div className="storeCreatGroup my-0" style={{ width: "auto" }}>
            <div className="storeCreatTextTitle d-flex align-items-center  ">
              <div className="" style={{ marginRight: "auto" }}>
                <p>職位權限名稱-甘蔗老董</p>
              </div>
              <div className="d-flex  ">
                {editInput ? (
                  <button
                    className="btn btn-primary mx-2 text-white"
                    onClick={() => {
                      editHandle();
                    }}
                  >
                    完成
                  </button>
                ) : (
                  <button
                    className="btn btn-primary mx-2 text-white"
                    onClick={() => {
                      editHandle();
                    }}
                  >
                    編輯
                  </button>
                )}
                <button
                  className="btn btn-danger mx-2 text-white"
                  onClick={() => {
                    deleteHandle();
                  }}
                >
                  刪除職位
                </button>
              </div>
            </div>
            <div className="">
