import React, { useState } from "react";
import ReactDOM from 'react-dom';
import ReactBsModal from "react-bootstrap/Modal";
import { ColorBtn } from "../../../components/ReactElement/ColorBtn"
import topArrew from "../../../images/svg/ui-icon/cil-arrow-top.svg"
import * as Managementdata from "../../../components/Json/ManagementType.json"
import {SwitchBtn} from "../../../components/ReactElement/SwitchBtn"

// ===== 分類管理的表格標題 =========
let TitleName = ["排序", "分類", "狀態", "操作"]
export function ManagementLabelTitle() {
    let ManagementLabelTitles = [];
    setTimeout(()=>{
        ManagemenData();
    },100)
    for (let x = 0; x < TitleName.length; x++) {
        ManagementLabelTitles.push(
            <th className={"ManagementTitle" + x}
                style={{
                    color: "#2F353A",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3",
                    textAlign: "left",
                    paddingLeft: "5px"
                }} key={x}><span>{TitleName[x]}</span>
                <img className="ManagementArrewTop"
                    alt=""
                    onClick={() => {
                        ManagementArrewChange(x);
                    }}
                    style={{
                        with: "3px",
                        height: "10px",
                        marginTop: "5px",
                        marginRight: "1px",
                        cursor: "pointer",
                        float: "right"
                    }}
                    src={topArrew}></img></th>
        )
    }
    return <thead style={{
        marginRight: "20px",
        marginLeft: "20px",
        marginTop: "10px"
    }}>
        <tr>
            {ManagementLabelTitles}
        </tr>
    </thead>
}

// =============分類管理的表格====================
export function ManagementLabel() {
    let LabelDatasTr = [];
    let LabelDatasTd = [];
    // 表格標題(包含點擊+圖片)
    for (let x = 0; x < TitleName.length; x++) {
        LabelDatasTd.push(<td
            className={"ManagementLabel" + x}
            style={{
                border: "1px solid #C8CED3",
                height:"50px"
            }}
            key={x}></td>)
    }
    // 表格欄位
    for (let z = 0; z < 3; z++) {
        LabelDatasTr.push(<tr 
            key={z}>{LabelDatasTd}</tr>)
    }
    return <tbody style={{
        marginRight: "20px",
        marginLeft: "20px",
        marginBottom: "100px"
    }}>
        {LabelDatasTr}
    </tbody>
}

//================分類管理警告視窗效果====================
export function ManagementBtn(props) {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <>
            <button
                className={props.className}
                style={{
                    height: "35px",
                    width: "80px",
                    lineHeight: "32px",
                    borderRadius: "4px",
                    fontSize: "14px",
                    backgroundColor: props.BtnBackgroundColor,
                    color: props.BtnColor,
                    cursor: "pointer",
                    border: props.BtnBorder,
                    marginRight: props.marginRight
                }
                } variant="primary" onClick={handleShow}>
                {props.BtnTitle}
            </button>
            <ReactBsModal show={show} onHide={handleClose}>
                <ReactBsModal.Header closeButton>
                    <ReactBsModal.Title className="ModalTitle">分類管理</ReactBsModal.Title>
                </ReactBsModal.Header>
                <h6 className="ManagemenName">建立分類名稱</h6>
                <input className="InputTheName" placeholder="請輸入分類名稱"></input>
                <ColorBtn className="BuildBtn " text="建立" color="white" backgroundColor="#00BFA5"
                    onClick={() => {
                        alert("建立完成")
                    }} />
                <ManagementLabelTitle />
                <ManagementLabel />
                <hr />
                <footer
                    style={{
                        display: "flex",
                        paddingBottom: "40px"
                    }}>
                    <div className="backBtns"
                        style={{
                            marginTop: "-5px"
                        }}>
                        <ColorBtn className="BackBtn" text="返回" backgroundColor="#C8CED3" variant="primary" onClick={handleClose} />
                        <ColorBtn className="PassBtn" text="通過" color="white" backgroundColor="#00BFA5"
                            onClick={() => {
                                alert("通過")
                            }} />
                    </div>
                </footer>
            </ReactBsModal>
        </>)
}

// 分類管理的表格標題箭頭效果
let isTopManagent = true;
function ManagementArrewChange(arrangement) {
    let ManagementArrewTop = document.getElementsByClassName("ManagementArrewTop");
    isTopManagent = !isTopManagent
    for (let x = 0; x < ManagementArrewTop.length; x++) {
        ManagementArrewTop[x].style.transform = "rotate(" + 0 + "deg)";
    }
    ManagementArrewTop[arrangement].style.transform = isTopManagent ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
}

function ManagemenData(){
       // =============分類管理 欄位內容引入==============
       let ManagementLabel0=document.getElementsByClassName("ManagementLabel0");
       let ManagementLabel1=document.getElementsByClassName("ManagementLabel1");
       let ManagementLabel2=document.getElementsByClassName("ManagementLabel2");
       let ManagementLabel3=document.getElementsByClassName("ManagementLabel3");
       for(let a=0; a<Managementdata.DataComponet.length;a++){
        ManagementLabel0[a].innerHTML=Managementdata.DataComponet[a]["Index"]
        ManagementLabel1[a].innerHTML=Managementdata.DataComponet[a]["Type"]
        ReactDOM.render(<SwitchBtn/>, ManagementLabel2[a]);
        ReactDOM.render(<ColorBtn text="刪除" backgroundColor="#F85756"/>, ManagementLabel3[a]);
       }
}
