import React, { useState } from "react";
import "../../styles/header.scss";
import { Link, useHistory } from "react-router-dom";
function Breadcrumb({
  paginationTitleOne,
  paginationTitleTwo,
  onePage,
  twoPage,
}) {
  const history = useHistory();
  const [status, setStatus] = useState([true, false]);
  const handleClass = (index) => {
    const newStatus = [false, false];
    newStatus[index] = true;
    setStatus(newStatus);
  };

  //這邊測試麵包穴
  // const pathnameList = ["/productlist", "/productdetail"];
  // const pathnameTextList = ["商品列表", "商品詳細"];
  // const convertPathnameToText = () => {
  //   console.log(props.location.pathname);
  //   // 尋找對應的索引值
  //   const index = pathnameList.indexOf(props.location.pathname);

  //   console.log(index);
  //   // 回傳中文的名稱
  //   return pathnameTextList[index];
  // };
  return (
    <div className="d-flex breadcrumb px-3 ">
      {paginationTitleOne && (
        <div style={{ marginRight: "auto" }} className="pagination">
          <div
            className={`paginationGroup ${status[0] ? "paginationOne" : "paginationTwo"
              }`}
          >
            <p
              onClick={() => {
                handleClass(0);
                history.push(onePage);
              }}
            >
              {paginationTitleOne}
            </p>
          </div>
          <div
            className={`paginationGroup ${status[1] ? "paginationOne" : "paginationTwo"
              }`}
          >
            <p
              onClick={() => {
                handleClass(1);
                history.push(twoPage);
              }}
            >
              {paginationTitleTwo}
            </p>
          </div>
        </div>
      )}
      <div className="d-flex" style={{
        marginRight: "16px"
      }}>
        {/* <p>Home / 機台商品</p> */}
        <ol className="d-flex">
          <li>
            <Link to="/">首頁</Link>
          </li>
          <li
            style={{
              margin: "0 5px",
            }}
          >
            {" "}
            /{" "}
          </li>
          <li>這裡</li>
        </ol>
      </div>
    </div>
  );
}

export default Breadcrumb;


export function CmsBreadcrumb(props) {
  return <div className="d-flex breadcrumb px-3 ">
    <div style={{ marginRight: "auto" }} className="CmsBreadcrumb">
      <div
        onClick={() => {
          localStorage.setItem("ShowManAll",0,6000)
          BreadcrumbSwitch();
        }} className="CmsBreadcrumbs">
        {props.p1}
      </div>
      <div style={{
        backgroundColor: "#f0f3f5",
        color: "#768192"
      }}
        onClick={() => {
          localStorage.setItem("ShowManAll",1,6000)
          BreadcrumbSwitch();   
        }}
        className="CmsBreadcrumbs">
        {props.p2}
      </div>
      <div style={{
        display: props.Btn3,
        backgroundColor: "#f0f3f5",
        color: "#768192"
      }}
        onClick={() => {
          localStorage.setItem("ShowManAll",2,6000)
          BreadcrumbSwitch();
        }}
        className="CmsBreadcrumbs">
        {props.p3}
      </div>
    </div>

    <div className="d-flex" style={{
      marginRight: "16px"
    }}>
      <ol className="d-flex">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li
          style={{
            margin: "0 5px",
          }}
        >
          {" "}
          /{" "}
        </li>
        <li>{props.AnyDoor}</li>
      </ol>
    </div>
  </div>
}

function BreadcrumbSwitch() {
  let CmsBreadcrumbs = document.getElementsByClassName("CmsBreadcrumbs");
  let ShowManAll = document.getElementsByClassName("ShowManAll");
  for (let x = 0; x < CmsBreadcrumbs.length; x++) {
    CmsBreadcrumbs[x].style.backgroundColor = "#f0f3f5";
    CmsBreadcrumbs[x].style.color = "#768192";
  }
  for (let y = 0; y < ShowManAll.length; y++) {
    ShowManAll[y].style.display = "none";
  }
  CmsBreadcrumbs[localStorage.ShowManAll].style.backgroundColor = "white";
  CmsBreadcrumbs[localStorage.ShowManAll].style.color = "rgba(0, 0, 0, 0.85)";
  if (ShowManAll.length > 0) {
    ShowManAll[localStorage.ShowManAll].style.display = "block";
  }
}
