import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Form, Row, Col } from "react-bootstrap";
import { Formik } from "formik";
import * as yup from "yup";

//定義錯誤訊息及驗證
const schema = yup.object().shape({
  store: yup.string().required("請選擇店家"),
  activity_name: yup.string().required("請輸入活動名稱"),
  startDate: yup.string().required("請選擇開始日期"),
  endDate: yup.string().required("請選擇結束日期"),
  buy_quantity: yup.number().required("請填入購買數量"),
  discount_amount: yup.number().required("請填入折抵金額"),
  discount_quantity: yup.number().required("請填入折抵數量"),

  total_budget: yup.number().required("請輸入總預算"),
});

function PlatformCost({
  startADay,
  setStartADay,
  toDayDate,
  endADay,
  setEndADay,
  maxDay,
}) {
  const history = useHistory();
  const [allStageChecked, setAllStageChecked] = useState(false);
  // const [discountCheck, setDiscountCheck] = useState("fixed_discount");

  // const discountCheckHandelChange = (e) => {
  //   setDiscountCheck(e.target.value);
  // };

  return (
    <Formik
      validationSchema={schema}
      onSubmit={(e) => {
        // setData([...data, e]);
        console.log(e);
      }}
      initialValues={{
        store: "",
        activity_name: "",
        startDate: "",
        endDate: "",
        buy_quantity: "",
        discount_amount: "",
        discount_quantity: "",
        discountRadio: "fixed_discount",
        total_budget: "",
      }}
    >
      {({ handleSubmit, handleChange, values, errors }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Group className="my-3 " as={Row} controlId="validationFormik03">
            <Form.Label column sm={2}>
              適用店家
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                as="select"
                placeholder="請選擇"
                name="store"
                value={allStageChecked ? (values.store = "All") : values.store}
                onChange={handleChange}
                isInvalid={!!errors.store}
                disabled={allStageChecked ? "disabled" : ""}
              >
                <option value="">請選擇</option>
                <option value="1">店家1</option>
                <option value="2">店家2</option>
                <option value="3">店家3</option>
              </Form.Control>
              <Form.Check
                type="checkbox"
                label="適用全平台"
                checked={allStageChecked}
                onChange={() => {
                  setAllStageChecked(!allStageChecked);
                }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.store}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>

          <Form.Group className="my-3" as={Row} controlId="validationFormik04">
            <Form.Label column sm={2}>
              活動名稱
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                placeholder="活動名稱"
                name="activity_name"
                value={values.activity_name}
                onChange={handleChange}
                isInvalid={!!errors.activity_name}
                maxLength="10"
              />
              <Form.Control.Feedback type="invalid">
                {errors.activity_name}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>

          <Form.Group className="my-3" as={Row} controlId="validationFormik06">
            <Form.Label column sm={2}>
              日期起訖
            </Form.Label>
            <div className="col-sm-10 col-md-10 d-flex flex-wrap">
              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="startDate"
                  value={startADay}
                  onChange={(e) => {
                    handleChange(e);
                    setStartADay(e.target.value);
                  }}
                  isInvalid={!!errors.startDate}
                  min={toDayDate}
                />
                <small style={{ color: "#768192" }}>
                  行銷活動最長只能持續七天
                </small>
                <Form.Control.Feedback type="invalid">
                  {errors.startDate}
                </Form.Control.Feedback>
              </Col>
              <Form.Label
                column
                md={1}
                className="d-flex justify-content-center "
              >
                ~
              </Form.Label>

              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="endDate"
                  value={endADay}
                  onChange={(e) => {
                    handleChange(e);
                    setEndADay(e.target.value);
                  }}
                  isInvalid={!!errors.endDate}
                  min={endADay}
                  max={maxDay}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.endDate}
                </Form.Control.Feedback>
              </Col>
            </div>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik11">
            <Form.Label column sm={2}>
              折扣方式
            </Form.Label>
            <Col sm={10} md={6}>
              <div
                className="d-flex "
                onChange={handleChange}
                value={values.discountRadio}
              >
                <Form.Check
                  className="me-3"
                  name="discountRadio"
                  type="radio"
                  value="fixed_discount"
                  defaultChecked={values.discountRadio === "fixed_discount"}
                  label="固定折價"
                  id="fixed_discount"
                />
                <Form.Check
                  className="me-3"
                  name="discountRadio"
                  type="radio"
                  value="percentage"
                  defaultChecked={values.discountRadio === "percentage"}
                  label="折扣百分比"
                  id="percentage"
                />
                <Form.Check
                  className="me-3"
                  name="discountRadio"
                  type="radio"
                  value="measure_up"
                  defaultChecked={values.discountRadio === "measure_up"}
                  label="滿額商品固定折價"
                  id="measure_up"
                />
              </div>
              <div className="d-flex align-items-center my-2">
                <p style={{ flexShrink: "0" }}>商品購買數量達</p>
                <Col className="mx-2">
                  <Form.Control
                    type="number"
                    name="buy_quantity"
                    value={values.buy_quantity}
                    onChange={handleChange}
                    isInvalid={!!errors.buy_quantity}
                    style={{ height: "40px" }}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.buy_quantity}
                  </Form.Control.Feedback>
                </Col>
                <p>個</p>
              </div>
              <div className="d-flex align-items-center my-2">
                <p style={{ flexShrink: "0" }}>服務費折</p>
                <Col className="mx-2">
                  <Form.Control
                    type="number"
                    name="discount_amount"
                    value={values.discount_amount}
                    onChange={handleChange}
                    isInvalid={!!errors.discount_amount}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.discount_amount}
                  </Form.Control.Feedback>
                </Col>
                <p style={{ flexShrink: "0" }}>元 X</p>
                <Col className="mx-2">
                  <Form.Control
                    type="number"
                    name="discount_quantity"
                    value={values.discount_quantity}
                    onChange={handleChange}
                    isInvalid={!!errors.discount_quantity}
                    // style={{ height: "40px" }}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.buy_quantity}
                  </Form.Control.Feedback>
                </Col>
                <p>個</p>
              </div>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik07">
            <Form.Label column sm={2}>
              總預算
            </Form.Label>
            <Col sm={5} md={3}>
              <Form.Control
                type="number"
                // as="number"
                name="total_budget"
                value={values.total_budget}
                onChange={handleChange}
                isInvalid={!!errors.total_budget}
              />
              <Form.Control.Feedback type="invalid">
                {errors.total_budget}
              </Form.Control.Feedback>
              <div style={{ color: "#768192" }}>
                選擇行銷活動費用上限，您只有在顧客訂餐時才須會支付費用，且支付金額絕對不會超過您的預算
              </div>
            </Col>

            <div className="col-md-3 col-sm-5" style={{ lineHeight: "38px" }}>
              元
            </div>
          </Form.Group>
          {/* 送出紐 */}
          <Form.Group
            as={Col}
            controlId="validationFormikUsername"
            className="d-flex justify-content-center my-3"
          >
            <button
              type="submit"
              className="btn btn-light  mx-2"
              onClick={() => {
                history.push("marketing");
              }}
            >
              返回
            </button>
            <button type="submit" className="btn btn-success text-white mx-2">
              確認
            </button>
          </Form.Group>
        </Form>
      )}
    </Formik>
  );
}

export default PlatformCost;
