import axios from "axios";
const AUTH_TOKEN =
  "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOlwvXC9hcGktdjItdC5oaWxsZXZlci5jb21cL2NtczJcL2FjY291bnRcL2xvZ2luIiwiaWF0IjoxNjM3NjMxMTkxLCJleHAiOjE2MzgyMzU5OTEsIm5iZiI6MTYzNzYzMTE5MSwianRpIjoiSmJ3aHhwTzk0RjJnejhQUCIsInN1YiI6MSwicHJ2IjoiZjY0ZDQ4YTZjZWM3YmRmYTdmYmY4OTk0NTRiNDg4YjNlNDYyNTIwYSJ9.Jfx3ACn4yHhQwhJ9xsZfc9Q49un5EzqJUQUFeIoLeuo";

const X_CSRF_TOKEN =
  "eyJpdiI6IitiaXFGSnJaYjVPZnpPTUc1UHg2VFE9PSIsInZhbHVlIjoiUTREbUk2Tm9TWHdNWDNHcU9aVDVBUmc3aks3cHQ5SVVURHZ4Ymk2MkhiWTVkZzJnRTM1cVRmaFB2NGdqWlBUbCIsIm1hYyI6IjgzMzJiMWU3MTgxM2MzMjIxMzA3NzU4NTZmNjQ3MmY3Mzg2NmQyYzhmODJmMGQ4ODc3NTllNjYzNTkyOGI3YjYifQ%3D%3D";
//公司資料API
const companyRequest = axios.create({
  baseURL: "https://api-v2-t.hillever.com/cms2/company",
});
//店家資料API
const storesRequest = axios.create({
  baseURL: "https://api-v2-t.hillever.com/cms2/stores",
});
//使用者資料API
const usersRequest = axios.create({
  baseURL: "https://api-v2-t.hillever.com/cms2/users",
});

const usersRequestCMS20 = axios.create({
  baseURL: "https://api-v2-t.hillever.com/cms2",
});
//圖片上傳API
const imgRequest = axios.create({
  baseURL: "https://api-v2-t.hillever.com/cms2/upload",
});
// token部分
companyRequest.defaults.headers.common = {
  Authorization: `Bearer ${AUTH_TOKEN}`,
  "X-CSRF-TOKEN": X_CSRF_TOKEN,
};
storesRequest.defaults.headers.common = {
  Authorization: `Bearer ${AUTH_TOKEN}`,
  "X-CSRF-TOKEN": X_CSRF_TOKEN,
};
usersRequest.defaults.headers.common = {
  Authorization: `Bearer ${AUTH_TOKEN}`,
  "X-CSRF-TOKEN": X_CSRF_TOKEN,
};
imgRequest.defaults.headers.common = {
  Authorization: `Bearer ${AUTH_TOKEN}`,
  "X-CSRF-TOKEN": X_CSRF_TOKEN,
  Accept: "application/json",
  "Content-Type": "application/json",
};
//公司資料API
export const companyShowApi = (id) => companyRequest.get("/show/" + id); //顯示單筆公司資料
export const companyEditApi = (data) =>
  companyRequest.post("/update/" + data.id, data); //更新單筆公司資料
export const companyCreatApi = (data) => companyRequest.post("/store", data); //新增單筆公司資料

//店家資料API
export const storesListApi = (data) =>
  storesRequest.post(
    `/list?offset=${data.offset}&limit=${data.limit}&sort_by=id&sort_rule=desc&search=`,
    data
  ); //顯示所有店家列表
export const storesShowApi = (id) => storesRequest.get("/show/" + id); //顯示單筆店家列表
export const storesCreatApi = (data) => storesRequest.post("/store", data); //新增單筆店家資料
export const storesEditApi = (data) =>
  storesRequest.post("/update/" + data.id, data); //更新單筆店家資料
export const storesDelectApi = (id) => storesRequest.delete("/destroy/" + id); //刪除單筆店家資料

//使用者資料API
export const account = (data) => usersRequestCMS20.post("/account/login", data);
export const authVendorList = (data) =>
  usersRequest.get("/auth-vendor-list", data); //廠商列表
export const getAllVendor = (id) =>
  storesRequest.get("/getAll?vendor_id=" + id); //廠商對應的店家ID
export const apiLists = (data) => usersRequest.post("/list", data); //使用者列表

//圖片上傳
export const imgUploadApi = (data) => imgRequest.post("/s3", data);

