import * as FinanceLabel from "../../../components/Json/FinanceLabel.json"
import ReactDOM from 'react-dom';
// 引入按鈕
import { ClickMe } from "../../../components/ReactElement/ColorBtn";
// 引入函式
import { UnfoldTd } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function ChangeWorld() {
  // ================監控區==================
  let DataTitle = ["排序", "日期", "匯款廠商", "匯款金額"];
  let context = document.getElementsByClassName("context")[0];
  let LabelTitleText = document.getElementsByClassName("LabelTitleText");
  let clickMeSpan0 = document.getElementsByClassName("clickMeSpan0");
  let clickMeText0 = document.getElementsByClassName("clickMeText0");
  let ChildTitle0 = document.getElementsByClassName("ChildTitle0")[0];
  let ChildTd1 = document.getElementsByClassName("ChildTd1");
  let ChildTd2 = document.getElementsByClassName("ChildTd2");
  let ChildTd3 = document.getElementsByClassName("ChildTd3");
  let ChildGroupTitle3 = document.getElementsByClassName("ChildGroupTitle3");
  let ChildGroupContent3 = document.getElementsByClassName("ChildGroupContent3");
  let inputCombo = document.getElementsByClassName("inputCombo");
  let SelectMe = document.getElementsByClassName("SelectMe");

  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "Finance") {
    // ================Label頁面微調CSS=======================
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    context.style.paddingBottom = "20px";
    ChildTitle0.style.width = "50px";
    ChildTitle0.style.textAlign = "center";
    ChildTitle0.style.padding = "3px 0";
    inputCombo[0].className="inputCombo inputComboFinance"

    // 表單標題文字引入
    for (let x = 0; x < DataTitle.length; x++) {
      LabelTitleText[x].innerHTML = DataTitle[x];
    }
    //表單欄位內容引入
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      clickMeText0[i].innerHTML = FinanceLabel.DataCompent[i]["Index"];
      ChildTd1[i].innerHTML = FinanceLabel.DataCompent[i]["Date"];
      ChildTd2[i].innerHTML = FinanceLabel.DataCompent[i]["Store"];
      ChildTd3[i].innerHTML = FinanceLabel.DataCompent[i]["Amount"];
      ChildGroupTitle3[i].innerHTML = DataTitle[3];
      ChildGroupContent3[i].innerHTML = FinanceLabel.DataCompent[i]["Amount"];
      ReactDOM.render(<ClickMe onClick={() => { UnfoldTd(i) }} />, clickMeSpan0[i]);
    }
  }
}
