import React, { useState } from "react";
// import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Index from "./pages";
import "./App.css";
//這邊放hearder && Footer
//SideBar
import { Sidebar } from "./components/ReactElement/Sidebar";
//這邊放hearder
import Header from "./components/ReactElement/Header";
import { Footer } from "./components/ReactElement/Footer";
//讓切換視窗時 永遠回到最上面
import ScrollToTop from "./components/ScollToTop";
// 各式手寫組件
import {
  DateInputGroup,
  SortingInputGroup,
  ExportInputGroup,
} from "./components/ReactElement/InputCombo";
//麵包屑2
import Breadcurmb from "./components/ReactElement/Breadcrumb";
import { CmsBreadcrumb } from "./components/ReactElement/Breadcrumb";
// 表格
import { NormalLabel } from "./pages/cms/Label/LabelWeb";
import { Reconciliation } from "./pages/cms/Report/Reconciliation";
import { SalesReport } from "./pages/cms/Report/SalesReport";
//這邊用於引入頁面
// 餐點總覽
import { OverView } from "../src/pages/cms/Menu/OverView";
//運補管理
import ReplenishmentOrder from "./pages/cms/ReplenishmentManagement/ReplenishmentOrder";
import MachineMerchandise from "./pages/cms/ReplenishmentManagement/MachineMerchandise";
import MachineMerchandiseEdit from "./pages/cms/ReplenishmentManagement/MachineMerchandiseEdit";
//群組與權限設定
import UserGroups from "./pages/cms/UserGroups/UserGroups";
//行銷管理
import Marketing from "./pages/cms/Marketing/Marketing";
import MarketingCreat from "./pages/cms/Marketing/MarketingCreat";
// 訂單管理
import { OrderManagement } from "./pages/cms/OrderManagement/OrderManagement"
//店家相關都放這
import Store from "./pages/cms/Store/Store";
import StoreCreat from "../src/pages/cms/Store/StoreCreat";
import StoreEdit from "../src/pages/cms/Store/StoreEdit";
import StoreUserEdit from "../src/pages/cms/Store/StoreUserEdit";
import StoreCompanyEdit from "../src/pages/cms/Store/StoreCompanyEdit";
import { Menber } from "./pages/Menber/Menber";
import { LoginSelect } from "./pages/Menber/LoginSelect";
//機器管理
import MachineNotificationSetting from "./pages/cms/Machine/MachineNotificationSetting";
import MachineNotification from "./pages/cms/Machine/MachineNotification";
import MachineSettings from "./pages/cms/Machine/MachineSettings";
import MachineRemote from "./pages/cms/Machine/MachineRemote";
import MachineCreat from "./pages/cms/Machine/MachineCreat";
// 新增商品項目
import { AddProduct } from "./pages/cms/Menu/AddProduct";
// 上架管理
import { PutSitting } from "../src/pages/cms/Put/putSitting";
// 財務管理
import { Finance } from "../src/pages/cms/Finance/Finance";

function App() {
  const [sideBarOnOff, setSideBarOnOff] = useState(true);
  return (
    <Router>
      <ScrollToTop>
        <Switch>
          {/* ===============首頁================= */}
          <Router exact path="/">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="首頁"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <Index />
              </div>
            </div>
          </Router>
          {/* ===============商店創建================= */}
          <Route path="/storecreat">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="店家管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <StoreCreat />
              </div>
            </div>
          </Route>
          {/* ===============商店編輯店家資料========== */}
          <Route path="/storeedit/:storeid">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="編輯店家資料"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <StoreEdit />
              </div>
            </div>
          </Route>
          {/* ==============編輯公司資料============= */}
          <Route path="/storecompanyedit/:companyid">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="編輯公司資料"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <StoreCompanyEdit />
              </div>
            </div>
          </Route>
          {/* ===============編輯使用者資料================ */}
          <Route path="/storeuseredit">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="編輯使用者資料"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <StoreUserEdit />
              </div>
            </div>
          </Route>
          {/* =================店家管理====================*/}
          <Route path="/store">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="店家管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <Store />
              </div>
            </div>
          </Route>
          {/* ===============登入==================== */}
          <Route path="/Menber">
            <Menber />
            <Footer />
          </Route>
          {/* ===============登入選擇頁面==================== */}
          <Route path="/LoginSelect">
            <LoginSelect />
            <Footer />
          </Route>
          {/* ===============群組與權限設定==================== */}
          <Route path="/usergroups">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="群組與權限設定"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <UserGroups />
              </div>
            </div>
          </Route>
          {/* ===============訂單管理==================== */}
          <Route path="/OrderManagement/create">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="訂單管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <CmsBreadcrumb
                  p1="被店家取消"
                  p2="店家未完成訂單"
                  p3="餐點準備"
                  />
                <OrderManagement />
              </div>
            </div>
          </Route>
          {/* ===============運補管理==================== */}
          <Route path="/replenishmentorder">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="運補管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb
                  paginationTitleOne={"補貨單"}
                  paginationTitleTwo={"歷史補貨紀錄"}
                  onePage={"/replenishmentorder"}
                  twoPage={"/machinemerchandise-edit"}
                />
                <ReplenishmentOrder />
              </div>
            </div>
          </Route>
          <Route path="/machinemerchandise-edit">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機台商品"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <MachineMerchandiseEdit />
              </div>
            </div>
          </Route>
          <Route path="/machinemerchandise">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機台商品"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <MachineMerchandise />
              </div>
            </div>
          </Route>
          {/* ===============行銷管理==================== */}
          <Route path="/marketing/creat">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="行銷管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <MarketingCreat />
              </div>
            </div>
          </Route>
          <Route path="/marketing">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="行銷管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <Marketing />
              </div>
            </div>
          </Route>

          {/* ===============機器管理==================== */}
          <Route path="/machinesettings">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機台設定"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb
                  paginationTitleOne={"機器管理"}
                  paginationTitleTwo={"遠端指令"}
                  onePage={"/machinesettings"}
                  twoPage={"/machinesremote"}
                />
                <MachineSettings />
              </div>
            </div>
          </Route>
          <Route path="/machinesremote">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機台設定"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb
                  paginationTitleOne={"機器管理"}
                  paginationTitleTwo={"遠端指令"}
                  onePage={"/machinesettings"}
                  twoPage={"/machinesremote"}
                />
                <MachineRemote />
              </div>
            </div>
          </Route>
          <Route path="/machinecreat">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機台設定"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <MachineCreat />
              </div>
            </div>
          </Route>
          <Route path="/machinenotification">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機器通知"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <MachineNotification />
              </div>
            </div>
          </Route>
          <Route path="/machinenotificationsetting">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="機器通知"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <MachineNotificationSetting />
              </div>
            </div>
          </Route>
          {/* ===============菜單總覽==================== */}
          <Route path="/Menu/Overview">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="餐點總覽"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <OverView />
              </div>
            </div>
          </Route>
          {/* ===============新增品項==================== */}
          <Route path="/Menu/Add">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="餐點總覽"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <AddProduct />
              </div>
            </div>
          </Route>
          {/* ===============上架管理==================== */}
          <Route path="/Menu/Put">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="上架設定"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                   <CmsBreadcrumb
                  p1="餐點結單"
                  p2="餐點時段設定"
                  Btn3="none"
                  AnyDoor="上架設定"
                  />
                <PutSitting />
              </div>
            </div>
          </Route>
          {/* ===============一般列表==================== */}
          <Route path="/Label/Date">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="列表單"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <DateInputGroup />
                <NormalLabel />
              </div>
            </div>
          </Route>
          {/* ===============Tab列表==================== */}
          <Route path="/Label/Sorting">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="列表單"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb
                  paginationTitleOne={"分頁一"}
                  paginationTitleTwo={"分頁二"}
                  paginationTitleThree={"分頁三"}
                />
                <SortingInputGroup />
                <NormalLabel />
              </div>
            </div>
          </Route>
          {/* ===============內頁-編輯頁==================== */}
          <Route path="/Inside/Edit">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="內頁-編輯頁"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
              </div>
            </div>
          </Route>
          {/* ===============內頁-檢視頁==================== */}
          <Route path="/Inside/View">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="列表單-檢視頁"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
              </div>
            </div>
          </Route>
          {/* ===============報表管理-對帳單==================== */}
          <Route path="/Report/Reconciliation">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="對帳單"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <SortingInputGroup />
                <Reconciliation />
              </div>
            </div>
          </Route>
          {/* ===============報表管理-銷售報表==================== */}
          <Route path="/Report/SalesReport">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="銷售報表"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <ExportInputGroup />
                <SalesReport />
              </div>
            </div>
          </Route>

          {/* ===============財務管理==================== */}
          <Route path="/Finance">
            <div className="container1 d-flex">
              {sideBarOnOff && (
                <div className="leftSideBar">
                  <Sidebar />
                </div>
              )}
              <div className="context">
                <Header
                  titleText="財務管理"
                  sideBarOnOff={sideBarOnOff}
                  setSideBarOnOff={setSideBarOnOff}
                />
                <Breadcurmb />
                <Finance />
              </div>
            </div>
          </Route>
        </Switch>
      </ScrollToTop>
    </Router>
  );
}
export default App;
