/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";
import * as Exampledata from "../../../components/Json/ExampleLabel2.json";
import { useHistory } from "react-router-dom";
import "../../../styles/store/store.scss";
// import EditRestaurant from "../../../components/ReactElement/EditRestaurant";
//撈取API
import {
  storesListApi,
  storesShowApi,
  // companyShowApi,
} from "../../../middleware/api";
// 表格
import { LabelSelect } from "../../../components/ReactElement/InputCombo";
import { LabelInput } from "../../../components/ReactElement/InputCombo";
// import { DateInput } from "../../../components/InputCombo";
// import { NormalLabel } from "../Label/LabelWeb";
// 按鈕素體
import { ColorBtn } from "../../../components/ReactElement/ColorBtn";
//左側商店列表
import StoreList from "../../../components/Store/StoreList";
// import StoreUser from "../../../components/Store/StoreUser";
// import { Label } from "../../../components/Label";
//上方店家
import StoreTopCompany from "../../../components/Store/StoreTopCompany";
//引入背景圖
import background from "../../../images/images/background/Nodata.png";
// import cilPencil from "../../../images/svg/ui-icon/cil-pencil.svg";
// import cilTrash from "../../../images/svg/ui-icon/cil-trash.svg";
import { ComponentTable } from "../../../components/Machine/ComponentTable";
function Store() {
  const history = useHistory();
  const [storeClickId, setStoreClickId] = useState(1);
  // const [userList, setUserList] = useState(1);
  // const [companyName, setCompanyName] = useState(null);
  //顯示單筆公司資料
  // const [companyShow, setCompanyShow] = useState([]);
  //顯示所有店家資料
  const [storeList, setStoreList] = useState([]);
  //顯示單筆資料
  const [storesShowList, setStoresShowList] = useState(1);
  //店家資
  // const [roomProperty, setRoomProperty] = useState([
  //   "日韓",
  //   "健康餐",
  //   "甜品",
  //   "中華料理",
  // ]);
  const [roomProperty] = useState(["日韓", "健康餐", "甜品", "中華料理"]);

  //這邊用來取得所有API
  const getStoreListAPI = async () => {
    const data = {
      draw: 1,
      offset: 0,
      limit: 10,
      sort_by: "id",
      sort_rule: "desc",
      custom: {
        search: null,
      },
    };
    try {
      const repStoreList = await storesListApi(data);
      setStoreList(repStoreList.data.data);
      const repStoreShow = await storesShowApi(storeClickId);
      setStoresShowList(repStoreShow.data.data.dataInfo);
      // const repCompanyShow = await companyShowApi(1);
      // setCompanyShow(repCompanyShow.data.data.dataInfo);
      // await storesShowApi(storeClickId).then(res => setStoresShowList(res.data.data.dataInfo))
    } catch (error) {
      console.log("異常", error);
    }
  };
  useEffect(() => {
    getStoreListAPI();
  }, []);
  useEffect(() => {
    getStoreListAPI();
  }, [storeClickId]);
  // console.log("所有店家列表", storeList);
  console.log("特定店家列表", storesShowList);
  // console.log("特定公司名稱", companyShow);

  return (
    <div className="storeContext">
      <div className="storeGroup">
        <StoreList
          storeList={storeList}
          setStoreClickId={setStoreClickId}
          storeClickId={storeClickId}
        />
        <div className="storeRight">
          <StoreTopCompany
            getStoreListAPI={getStoreListAPI}
            roomProperty={roomProperty}
            background={background}
            setStoreClickId={setStoreClickId}
            storeClickId={storeClickId}
            storesShowList={storesShowList}
          />
          <button
            onClick={() => {
              history.push(`/storecompanyedit/${2}`);
            }}
          >
            編輯公司資料
          </button>
          <button
            onClick={() => {
              history.push(`/storeuseredit/${1}`);
            }}
          >
            編輯使用者資料
          </button>
          {/* {userList ? <Label /> : <img src={background} alt="" />} */}
          <div className="iunputCombo">
            <LabelSelect p="店家選擇" />
            <LabelInput p="關鍵字查詢" placeholder="請輸入您的姓名" />
          </div>
          <div className="SearchAddBtns">
            <ColorBtn
              className="SearchBtn"
              text="搜尋"
              backgroundColor="#1985AC"
            />
            <ColorBtn
              className="RightBtn"
              text="新增"
              backgroundColor="#00BFA5"
            />
          </div>
          {/* <NormalLabel /> */}
          <ComponentTable
            data={Exampledata.DataCompent}
            dataOrder={[
              "Index",
              "Account",
              "Authority",
              "User",
              // "Amount",
              // "Type",
              "Button",
            ]}
            dataHeard={[
              "排序",
              "帳號",
              "職位權限",
              "使用者",
              // "金額",
              // "訂付款方式",
              "操作",
            ]}
          />
        </div>
      </div>
    </div>
  );
}

export default Store;
