import React from "react";
import 'antd/dist/antd.css';
import "../../styles/ReactElement/DataUpload.css"
import { Upload } from 'antd';
import { LongColorBtn } from "./ColorBtn"

export function UploadInput(props) {
    return <div className={"UploadInput "+props.className}>
        <p>{props.p}</p>
        <input></input>
        <Upload onClick={() => {
            setTimeout(() => {
                KillRepeatPic();
            }, 3000);
        }}{...props}>
            <LongColorBtn
                text={"選擇檔案"}
                color="#2F353A"
            ></LongColorBtn >
        </Upload>
        <h6>{props.specification}</h6>
    </div>
}

// 選擇圖片  若是已有選過就會刪除原有圖片
function KillRepeatPic() {
    let repeatPic=document.getElementsByClassName("ant-upload-list-text-container");
    if (repeatPic.length >= 1) {
        repeatPic[0].remove();
    }
}
