import React from "react";
import { RemoveSidebarOption, RemoveLabel } from "../components/ReactElement/RemoveLocalStorage"

function Index() {
  RemoveSidebarOption();
  RemoveLabel();
  return <div>
  </div>;
}
export default Index;

// ======滾動滑鼠監聽事件======
window.addEventListener("scroll", () => {
  let scrollable = document.documentElement.scrollHeight - window.innerHeight;
  let scrolled = window.scrollY;
  if (window.location.href.split("http://localhost:3500/")[1] !== "menber") {
    if ((scrollable - scrolled) <= 50) {
      if (window.location.href !== "http://localhost:3500/loginSelect" &&
        window.location.href !== "http://localhost:3500/menber") {
        let context = document.getElementsByClassName("context")[0];
        context.className = "context haha";
      }
    }
    else {
      let context = document.getElementsByClassName("context")[0];
      if (window.location.href !== "http://localhost:3500/loginSelect" &&
        window.location.href !== "http://localhost:3500/menber") {
        context.className = "context";
      }
    }
  }
});
