import React from "react";
import { CheckPW, DontForgot, ChangePW } from "./passwordGoalkeeper"
import { passwordShow, passwordHide } from "./lookPassword"
import "../../styles/menber/menber.css";
// 引用元件
import ReactBsForm from "react-bootstrap/Form";
// 引用圖片
import MailIcon from "../../images/svg/ui-icon/cil-envelope-closed.svg";
import LockIcon from "../../images/svg/ui-icon/cil-lock-locked.svg";
import EyeIcon from "../../images/svg/ui-icon/eyes.svg";

// Label && input 模塊
export function ReactBsFormGroup(props) {
  return (
    <ReactBsForm.Group className="mb-3" controlId="formBasicEmail">
      <ReactBsForm.Label className="MenberLabel">
        {props.LabelContent}
      </ReactBsForm.Label>
      <ReactBsForm.Control
        type={props.inputType}
        value={props.value}
        placeholder={props.placeholder}
        className={props.InputclassName}
      />
      <img alt="" className="InputIcon" src={props.iconSrc}></img>
      <div
        className="passwordEye"
        onMouseDown={() => (
          passwordShow()
        )}
        onMouseUp={() => {
          passwordHide()
        }}
        style={{
          display: props.eyeDisplay
        }}><img
          alt=""
          src={EyeIcon}
        ></img></div>
    </ReactBsForm.Group >
  );
}

export function Menber() {
  //  =====================Menber 登入頁面==============================
  if (window.location.href === "http://localhost:3500/menber") {
    if (localStorage.TK !== undefined) {
      window.location.href = "http://localhost:3500";
    }
    else {
      return <div className="MenberCombo">
        <h1 className="MenberTitle">Cloud Management System</h1>
        <ReactBsForm
          className="MenberForm"
          onClick={(e) => {
            e.preventDefault();
            CheckPW();
          }}
        >
          <ReactBsFormGroup
            LabelContent="帳號"
            InputclassName="whoU"
            inputType="mail"
            iconSrc={MailIcon}
            eyeDisplay="none"
            placeholder="ex-CMS-Admin@hillcorp.com"
          />
          <ReactBsFormGroup
            LabelContent="密碼"
            InputclassName="whoU"
            inputType="password"
            iconSrc={LockIcon}
          />
          <button
            className="SubmitBtn"
            variant="primary"
            type="submit"
          >
            送出
          </button>
          <h6 className="AlertText">請給我帳號密碼</h6>
          <h6
            className="forgetPW"
            onClick={() => {
              window.location.href = "http://localhost:3500/menber/forgetPW";
            }}
          >
            忘記密碼?
          </h6>
        </ReactBsForm>
        <div className="helpYou">
          <h5>
            <span className="questionMarkIcon">?</span>登入問題?
          </h5>
          <h6>
            任何登入相關問題歡迎來信
            <span>
              <a href="/">inquiry@hillcorp.com</a>
            </span>
          </h6>
        </div>
      </div>
    }
  }
  //  =====================Menber 忘記密碼頁面==============================
  else if (window.location.href === "http://localhost:3500/menber/forgetPW") {
    return (
      <div>
        <div className="MenberCombo">
          <h1 className="MenberTitle">忘記密碼</h1>
          <div className="MenberForm">
            <ReactBsFormGroup
              LabelContent="帳號"
              inputType="mail"
              iconSrc={MailIcon}
              eyeDisplay="none"
              placeholder="ex-CMS-Admin@hillcorp.com"
            />
            <button
              className="SubmitBtn"
              variant="primary"
              type="submit"
              onClick={(e) => {
                e.preventDefault();
                DontForgot();
              }}
            >
              送出
            </button>
            <h6 className="AlertText">請給我帳號密碼</h6>
            <button
              className="SubmitBtn"
              variant="primary"
              type="submit"
              onClick={(e) => {
                window.history.go(-1);
              }}>
              上一頁
            </button>
          </div>
        </div>
      </div>
    );
  }
  //  =====================Menber 更改密碼頁面==============================
  else if (window.location.href === "http://localhost:3500/menber/changePW") {
    return <div>
      <div className="MenberCombo">
        <h1 className="MenberTitle">更改密碼</h1>
        <div className="MenberForm">
          <ReactBsFormGroup
            InputclassName="whoU"
            LabelContent="帳號"
            inputType="mail"
            iconSrc={MailIcon}
            eyeDisplay="none"
            placeholder="ex-CMS-Admin@hillcorp.com"
          />
          <ReactBsFormGroup
            InputclassName="whoU"
            LabelContent="密碼"
            inputType="password"
            iconSrc={LockIcon}
          />
          <ReactBsFormGroup
            InputclassName="whoU"
            LabelContent="再次輸入密碼"
            inputType="password"
            iconSrc={LockIcon}
          />
          <button
            className="SubmitBtn"
            variant="primary"
            type="submit"
            onClick={(e) => {
              e.preventDefault();
              ChangePW();
            }}
          >
            送出
          </button>
          <h6 className="AlertText">請給我帳號密碼</h6>
          <h6 onClick={() => {
            window.location.href = "http://localhost:3500/menber/forgetPW";
          }} className="forgetPW">忘記密碼?</h6>
          <button
            className="SubmitBtn"
            variant="primary"
            type="submit"
            onClick={(e) => {
              window.history.go(-1);
            }}
          >
            上一頁
          </button>
        </div>
      </div>
    </div>
  }
}

// ================調整Menber頁面的CSS樣式==============
(function SwichBodyStyle() {
  if (
    window.location.href==="http://localhost:3500/menber"||
    window.location.href==="http://localhost:3500/LoginSelect"
    ) {
    document.body.style.backgroundColor = "white";
    document.body.className = "h0";
  }
})();
