import React, { useState } from "react";
import ReactDOM from 'react-dom';
import ReactBsModal from "react-bootstrap/Modal";
import { ColorBtn } from "../../../components/ReactElement/ColorBtn"
import topArrew from "../../../images/svg/ui-icon/cil-arrow-top.svg"
import { SwitchBtn } from "../../../components/ReactElement/SwitchBtn"

// ===== 分類管理的表格標題 =========
export function ManagementLabelTitle() {
    let TitleName = ["排序", "分類", "狀態", "操作"];
    let ManagementLabelTitles = TitleName.map((TitleName, x) => 
    <th className={"ManagementTitle" + x}
        style={{
            color: "#2F353A",
            fontSize: "14px",
            fontWeight: "bolder",
            backgroundColor: "#E7EBEF",
            border: "1px solid #C8CED3",
            textAlign: "left",
            paddingLeft: "5px"
        }} key={x}><span>{TitleName}</span>
        <img className="ManagementArrew"
            alt=""
            onClick={() => {
                ManagementArrewChange(x);
            }}
            style={{
                with: "3px",
                height: "10px",
                marginTop: "5px",
                marginRight: "1px",
                cursor: "pointer",
                float: "right"
            }}
            src={topArrew}></img></th>);
    return <thead style={{
        marginRight: "20px",
        marginLeft: "20px",
        marginTop: "10px"
    }}>
        <tr>
            {ManagementLabelTitles}
        </tr>
    </thead>
}

// =============分類管理的表格====================
export function ManagementLabel() {
    let ListData = {
        draw: 1,
        offset: 0,
        limit: 99,
        sort_by: "id",
        sort_rule: "desc",
        custom: {
            search: null
        }
    };
    fetch("https://api-v2-t.hillever.com/cms2/item/category/list", {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            'Authorization': `Bearer ${localStorage.UserTK}`
        },
        body: JSON.stringify(ListData),
    })
        .then((response) => {
            return response.json();
        })
        .then((res) => {
            let ManagementLabel = document.getElementsByClassName("ManagementLabel");
            let LabelDatasTr = [];
            if (ManagementLabel.length > 0) {
                for (let x = 0; x < res.data.length; x++) {
                    let LabelDatasTd = [
                        x,
                        res.data[x]["name"],
                        <SwitchBtn />,
                        <ColorBtn text="刪除" backgroundColor="#F85756" />];
                    let LabelDatasTds = LabelDatasTd.map((LabelDatasTd, y) =>
                        <td className={"ManagementLabel" + y}
                            style={{
                                border: "1px solid #C8CED3",
                                height: "50px"
                            }} key={y}>{LabelDatasTd}</td>)
                    for (let z = 0; z < 1; z++) {
                        LabelDatasTr.push(<tr
                            key={z}>{LabelDatasTds}</tr>)
                    }
                    ReactDOM.render(<>{LabelDatasTr}</>, ManagementLabel[0])
                }
            }
        });
}

//================分類管理警告視窗效果====================
export function ManagementBtn(props) {
    let [show, setShow] = useState(false);
    let handleClose = () => setShow(false);
    let handleShow = () => setShow(true);
    return <>
        <button
            className={props.className}
            style={{
                height: "35px",
                width: "80px",
                lineHeight: "32px",
                borderRadius: "4px",
                fontSize: "14px",
                backgroundColor: props.BtnBackgroundColor,
                color: props.BtnColor,
                cursor: "pointer",
                border: props.BtnBorder,
                marginRight: props.marginRight
            }
            } variant="primary" onClick={() => {
                handleShow();
                ManagementLabel();
                setTimeout(()=>{
                    document.body.style.overflow = "auto";
                    document.body.style.paddingRight = "0px"
                },10)
            }}>
            {props.BtnTitle}
        </button>
        <ReactBsModal show={show} onHide={handleClose}>
            <ReactBsModal.Header closeButton>
                <ReactBsModal.Title className="ModalTitle">分類管理</ReactBsModal.Title>
            </ReactBsModal.Header>
            <h6 className="ManagemenName">建立分類名稱</h6>
            <input className="InputTheName" placeholder="請輸入分類名稱"></input>
            <ColorBtn className="BuildBtn " text="建立" color="white" backgroundColor="#00BFA5"
                onClick={() => {
                    alert("建立完成")
                }} />
            <table style={{
                marginRight: "20px",
                marginLeft: "20px",
                marginTop: "15px",
                marginBottom: "40px"
            }}>
                <ManagementLabelTitle />
                <tbody className="ManagementLabel"></tbody>
            </table>
            <hr />
            <footer
                style={{
                    display: "flex",
                    paddingBottom: "40px"
                }}>
                <div className="backBtns"
                    style={{
                        marginTop: "-5px"
                    }}>
                    <ColorBtn className="BackBtn" text="返回" backgroundColor="#C8CED3" variant="primary" onClick={handleClose} />
                    <ColorBtn className="PassBtn" text="通過" color="white" backgroundColor="#00BFA5"
                        onClick={() => {
                            alert("通過")
                        }} />
                </div>
            </footer>
        </ReactBsModal>
    </>
}

// 分類管理的表格標題箭頭效果
let isManagent = true;
function ManagementArrewChange(arrangement) {
    let ManagementArrew = document.getElementsByClassName("ManagementArrew");
    isManagent = !isManagent
    for (let x = 0; x < ManagementArrew.length; x++) {
        ManagementArrew[x].style.transform = "rotate(" + 0 + "deg)";
    }
    ManagementArrew[arrangement].style.transform = isManagent ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
}
