import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/Machine/Machine.scss";
import SwitchButton from "../ReactElement/SwitchButton";
import { CChart } from "@coreui/react-chartjs";
//引入複數表單
// import MachineFrom from "../Machine/MachineFrom";
function MachineTemp({
  buttonTitle,
  buttonClassName,
  modalTitle,
  enterColor,
  closeColor,
  removeColor,
}) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  // const [radioValue, setRadioValue] = useState({
  //   radioValueState: "",
  // });
  // const handleChange = (e) => {
  //   console.log(e.target.value);
  //   if (e.target.value === radioValue.radioValueState) {
  //     setRadioValue({ radioValueState: "" });
  //   } else {
  //     setRadioValue({ radioValueState: e.target.value });
  //   }
  // };

  return (
    <>
      <Button variant={buttonClassName} onClick={handleShow}>
        {buttonTitle}
      </Button>

      <Modal
        size="lg"
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h4 style={{ fontWeight: "bold" }}>{modalTitle}</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="">
            <div className="d-flex justify-content-between py-3">
              <p>日期起訖</p>
              <div className="d-flex flex-column w-25 ">
                <input type="date" className="componentsInput" />
                <span className="componentsSpan">日期起始以三個月內為限</span>
              </div>
              <div className="flex-shrink-1">-</div>
              <div className="d-flex flex-column w-25">
                <input type="date" className="componentsInput" />
                <span className="componentsSpan">日期結束以三個月內為限</span>
              </div>
              <div className="me-5">
                <button className="btn btn-primary text-white">搜尋</button>
              </div>
            </div>
            <CChart
              type="line"
              data={{
                labels: [
                  "January",
                  "February",
                  "March",
                  "April",
                  "May",
                  "June",
                  "July",
                ],
                datasets: [
                  {
                    label: "My First dataset",
                    backgroundColor: "rgba(220, 220, 220, 0.2)",
                    borderColor: "rgba(220, 220, 220, 1)",
                    pointBackgroundColor: "rgba(220, 220, 220, 1)",
                    pointBorderColor: "#fff",
                    data: [40, 20, 12, 39, 10, 40, 39, 80, 40],
                  },
                  {
                    label: "My Second dataset",
                    backgroundColor: "rgba(151, 187, 205, 0.2)",
                    borderColor: "rgba(151, 187, 205, 1)",
                    pointBackgroundColor: "rgba(151, 187, 205, 1)",
                    pointBorderColor: "#fff",
                    data: [50, 12, 28, 29, 7, 25, 12, 70, 60],
                  },
                ],
              }}
            />
            <table className="table table-bordered text-center mb-3">
              <tbody>
                <tr style={{ backgroundColor: "#e6ebef" }}>
                  <th scope="col">名稱</th>
                  <th scope="col">目前溫度(度)</th>
                  <th scope="col">高溫設定(度)</th>
                  <th scope="col">低溫設定(度)</th>
                  <th scope="col">
                    自動排程
                    <button
                      type="button"
                      className="btn btnTooltips"
                      data-bs-toggle="tooltip"
                      data-bs-placement="top"
                      title="機台營業時間前30分鐘自動開始"
                      style={{ width: "10px" }}
                    >
                      ?
                    </button>
                  </th>
                  <th scope="col">
                    手動開啟
                    <button
                      type="button"
                      className="btn btnTooltips"
                      data-bs-toggle="tooltip"
                      data-bs-placement="top"
                      title="營業中的機台無法手動開啟加熱版"
                      style={{ width: "10px" }}
                    >
                      ?
                    </button>
                  </th>
                </tr>
                <tr>
                  <td>加熱版</td>
                  <td>30</td>
                  <td>
                    <input
                      type="number"
                      className="componentsInput"
                      style={{ width: "100%" }}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="componentsInput"
                      style={{ width: "100%" }}
                    />
                  </td>
                  <td>
                    <SwitchButton />
                  </td>
                  <td>
                    <SwitchButton />
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <div className="d-flex w-100">
            <div className="me-auto">
              <Button
                variant="btn btn-warning warningText "
                onClick={handleClose}
              >
                更新
              </Button>
            </div>
            <div className="me-3">
              <Button variant={removeColor} onClick={handleClose}>
                返回
              </Button>
            </div>
            <div>
              <Button variant={enterColor}>確認</Button>
            </div>
          </div>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default MachineTemp;
