import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Swal from "sweetalert2";
import MachineErrorReturn from "../../../components/ReplenishmentManagement/MachineErrorReturn";
import "../../../styles/ReplenishmentManagement/replenishmentOrder.scss";
function MachineMerchandise() {
  const history = useHistory();
  //檢視用的狀態
  const [checking, setChecking] = useState(null);
  //切換回報狀態的狀態
  const [returnBtn, setReturnBtn] = useState(false);
  //取消異常用的按鈕
  const [cancelBtn, setCancelBtn] = useState(true);
  //取消異常用的子按鈕
  const [cancelBtnCheck, setCancelBtnCheck] = useState(false);
  //異常回報按鈕的狀態切換
  const onReturnBthHandle = () => {
    setReturnBtn(!returnBtn);
  };
  const onCancelBtn = () => {
    setCancelBtn(!cancelBtn);
  };
  //取消按鈕的子按鈕狀態切換
  const onCancelBtnCheck = () => {
    setCancelBtnCheck(!cancelBtnCheck);
  };
  return (
    <div className="container">
      <div className="MMCtitle d-flex flex-wrap">
        <div className="MMCtitleGroup ms-4">
          <p className="mb-2">主櫃</p>
          <select
            name=""
            id=""
            placeholder="請選擇"
            className="MMCselect"
          ></select>
        </div>
        <div className="MMCtitleGroup ms-4">
          <p className="mb-2">關鍵字查詢</p>
          <input type="text" placeholder="請輸入" className="MMCselect" />
        </div>
        <div className="MMCtitleGroup d-flex align-items-end ms-4">
          <button className="btn btn-primary text-white">搜尋</button>
        </div>
      </div>
      <div>
        <button
          className={
            returnBtn
              ? "btn  btn-secondary mx-4 my-4"
              : "btn btn-danger text-white mx-4 my-4"
          }
          onClick={onReturnBthHandle}
        >
          {returnBtn ? "取消回報" : "異常回報"}
        </button>

        {returnBtn && <MachineErrorReturn className="text-white mx-4 my-4" />}

        {cancelBtn && (
          <button
            className="btn btn-outline-danger"
            onClick={() => {
              onCancelBtnCheck();
              onCancelBtn();
            }}
          >
            取消異常
          </button>
        )}
        {cancelBtnCheck && (
          <>
            <button
              className="btn btn-secondary  mx-4 my-4"
              onClick={() => {
                onCancelBtnCheck();
                onCancelBtn();
              }}
            >
              返回
            </button>
            <button
              className="btn btn-success text-white  "
              onClick={() => {
                onCancelBtnCheck();
                // onCancelBtn();
                Swal.fire({
                  title: "異常取消成功",
                  icon: "success",
                  confirmButtonColor: "#009E88",
                  confirmButtonText: "確認",
                });
              }}
            >
              確認
            </button>
          </>
        )}
        {/* 機台商品表單 */}
        <div className="mx-4 mb-4">
          <table className="table table-bordered border  MMCtable">
            <tbody className="userGroupsTable">
              <tr style={{ backgroundColor: "#e6ebef" }}>
                {returnBtn && <th scope="row"></th>}
                <th scope="row">狀態</th>
                <th>機器識別碼</th>
                <th>機器名稱</th>
                <th>類型</th>
                <th>副櫃數量</th>
                <th>操作</th>
              </tr>
              <tr>
                {returnBtn && (
                  <td>
                    <input type="checkbox" />
                  </td>
                )}
                <td>
                  <p>啟用</p>
                </td>
                <td>
                  <p>H000000111</p>
                </td>
                <td>
                  <p>TOP科技大樓(大廳)</p>
                </td>
                <td>
                  <p>智能販賣機</p>
                </td>
                <td className="text-center">
                  <p>2</p>
                </td>
                <td className="text-center">
                  <button
                    className="btn btn-warning text-secondary"
                    onClick={() => {
                      checking === 1 ? setChecking(null) : setChecking(1);
                    }}
                  >
                    檢視
                  </button>
                </td>
              </tr>
              {/* 這邊隱藏 */}
              {checking === 1 && (
                <>
                  <tr
                    className="table-borderless"
                    style={{ backgroundColor: "#e6ebef" }}
                  >
                    {returnBtn && <th scope="row" className="border-0"></th>}
                    <th scope="row" className="border-0">
                      副櫃狀態
                    </th>
                    <th className="border-0">機器識別碼</th>
                    <th className="border-0">機器名稱</th>
                    <th className="border-0">類型</th>
                    <th className="border-0">副櫃數量</th>
                    <th className="border-0">操作</th>
                  </tr>
                  <tr style={{ backgroundColor: "#e6ebef" }}>
                    {returnBtn && (
                      <td className="border-0">
                        <input type="checkbox" />
                      </td>
                    )}
                    <td className="border-0">
                      <p>啟用</p>
                    </td>
                    <td className="border-0">
                      <p>HE001</p>
                    </td>
                    <td className="border-0">
                      <p>富貴1號</p>
                    </td>
                    <td className="border-0">
                      <p>智能販賣機</p>
                    </td>

                    <td className="text-center border-0" colSpan="2">
                      <button
                        className="btn btn-primary text-white"
                        onClick={() => {
                          history.push("/machinemerchandise-edit");
                        }}
                      >
                        機台商品編輯
                      </button>
                    </td>
                  </tr>
                  <tr style={{ backgroundColor: "#e6ebef" }}>
                    {returnBtn && (
                      <td className="border-0">
                        <input type="checkbox" />
                      </td>
                    )}
                    <td className="border-0">
                      <p>啟用</p>
                    </td>
                    <td className="border-0">
                      <p>HE002</p>
                    </td>
                    <td className="border-0">
                      <p>富貴2號</p>
                    </td>
                    <td className="border-0">
                      <p>智能販賣機</p>
                    </td>

                    <td className="text-center border-0" colSpan="2">
                      <button
                        className="btn btn-primary text-white"
                        onClick={() => {}}
                      >
                        機台商品編輯
                      </button>
                    </td>
                  </tr>
                  {/* <tr style={{ backgroundColor: "#e6ebef" }}>
                    <th scope="row">狀態</th>
                    <th>機器識別碼</th>
                    <th>機器名稱</th>
                    <th>類型</th>
                    <th>副櫃數量</th>
                    <th>操作</th>
                  </tr> */}
                </>
              )}
              <tr>
                <td>
                  <p>啟用</p>
                </td>
                <td>
                  <p>H000000111</p>
                </td>
                <td>
                  <p>TOP科技大樓(大廳)</p>
                </td>
                <td>
                  <p>智能販賣機</p>
                </td>
                <td className="text-center">
                  <p>2</p>
                </td>
                <td className="text-center">
                  <button
                    className="btn btn-warning text-secondary"
                    onClick={() => {
                      checking === 2 ? setChecking(null) : setChecking(2);
                    }}
                  >
                    檢視
                  </button>
                </td>
              </tr>
              {/* 這邊隱藏 */}
              {checking === 2 && (
                <>
                  <tr
                    className="table-borderless"
                    style={{ backgroundColor: "#e6ebef" }}
                  >
                    {returnBtn && <th scope="row" className="border-0"></th>}
                    <th scope="row" className="border-0">
                      副櫃狀態
                    </th>
                    <th className="border-0">機器識別碼</th>
                    <th className="border-0">機器名稱</th>
                    <th className="border-0">類型</th>
                    <th className="border-0">副櫃數量</th>
                    <th className="border-0">操作</th>
                  </tr>
                  <tr style={{ backgroundColor: "#e6ebef" }}>
                    {returnBtn && (
                      <td className="border-0">
                        <input type="checkbox" />
                      </td>
                    )}
                    <td className="border-0">
                      <p>啟用</p>
                    </td>
                    <td className="border-0">
                      <p>HE001</p>
                    </td>
                    <td className="border-0">
                      <p>富貴1號</p>
                    </td>
                    <td className="border-0">
                      <p>智能販賣機</p>
                    </td>

                    <td className="text-center border-0" colSpan="2">
                      <button
                        className="btn btn-primary text-white"
                        onClick={() => {
                          history.push("/machinemerchandise-edit");
                        }}
                      >
                        機台商品編輯
                      </button>
                    </td>
                  </tr>
                  <tr style={{ backgroundColor: "#e6ebef" }}>
                    {returnBtn && (
                      <td className="border-0">
                        <input type="checkbox" />
                      </td>
                    )}
                    <td className="border-0">
                      <p>啟用</p>
                    </td>
                    <td className="border-0">
                      <p>HE002</p>
                    </td>
                    <td className="border-0">
                      <p>富貴2號</p>
                    </td>
                    <td className="border-0">
                      <p>智能販賣機</p>
                    </td>

                    <td className="text-center border-0" colSpan="2">
                      <button
                        className="btn btn-primary text-white"
                        onClick={() => {}}
                      >
                        機台商品編輯
                      </button>
                    </td>
                  </tr>
                  {/* <tr style={{ backgroundColor: "#e6ebef" }}>
                    <th scope="row">狀態</th>
                    <th>機器識別碼</th>
                    <th>機器名稱</th>
                    <th>類型</th>
                    <th>副櫃數量</th>
                    <th>操作</th>
                  </tr> */}
                </>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default MachineMerchandise;
