import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Swal from "sweetalert2";
import MachineErrorReturn from "../../../components/ReplenishmentManagement/MachineErrorReturn";
import "../../../styles/ReplenishmentManagement/replenishmentOrder.scss";
//使用日期套件
// import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
function MachineMerchandiseEdit() {
  // const [startDate, setStartDate] = useState(new Date());
  const [errorBtn, setErrorBtn] = useState(false);
  const history = useHistory();
  return (
    <div className="container">
      <div className="mx-2 MMCEcontext">
        <div className="MMCEtitle py-2 px-3">機台資訊</div>
        <div className="MMCEbody my-2 mx-3 py-2 d-flex">
          <p className="w-25">主櫃機器識別碼</p>
          <p>HE000000</p>
        </div>
        <div className="MMCEbody my-2 mx-3 py-2 d-flex">
          <p className="w-25">副櫃機器識別碼</p>
          <p>HE000</p>
        </div>
        <div className="MMCEbody my-2 mx-3 py-2 d-flex">
          <p className="w-25">副櫃機器名稱</p>
          <p>富貴1號</p>
        </div>
        <div className="MMCEbody my-2 mx-3 py-2 d-flex">
          <p className="w-25">副櫃類型</p>
          <p>智販機</p>
        </div>
        <div className="MMCEbody my-2 mx-3 py-2 d-flex">
          <p className="w-25">副櫃貨道數量</p>
          <p>27</p>
        </div>
      </div>
      {!errorBtn ? (
        <button
          className="m-2 my-3 btn btn-danger text-white"
          onClick={() => setErrorBtn(!errorBtn)}
        >
          異常回報
        </button>
      ) : (
        <>
          <button
            className="m-2 my-3 btn btn-outline-danger"
            onClick={() => setErrorBtn(!errorBtn)}
          >
            取消回報
          </button>
          <MachineErrorReturn />
        </>
      )}
      <table className="MMCEtable table table-bordered mx-1 my-3 ">
        <tbody>
          <tr>
            {errorBtn && <th scope="col"></th>}
            <th scope="col">貨道</th>
            <th scope="col">Barcode/商品</th>
            <th scope="col">有效期限</th>
            <th scope="col">售價</th>
            <th scope="col">數量</th>
            <th scope="col">操作</th>
          </tr>

          <tr>
            {errorBtn && (
              <td>
                <input type="checkbox" name="" id="" />
              </td>
            )}
            <th scope="row">1</th>
            <td>
              <input type="text" />
            </td>
            <td>
              <div>
                <input
                  type="date"
                  className="MMCEdata aaadate"
                  name="aaadate"
                />
              </div>
            </td>
            <td>
              <input type="number" />
            </td>
            <td>
              <input type="number" />
            </td>
            <td>
              <button
                className="btn btn-danger text-white"
                onClick={() => {
                  Swal.fire({
                    title: "確認下架?",
                    text: "確認是否下架，下架後無法回復",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonColor: "#3085d6",
                    cancelButtonColor: "#ACB5BC",
                    confirmButtonText: "下架",
                    cancelButtonText: "返回",
                  }).then((result) => {
                    if (result.isConfirmed) {
                      Swal.fire({
                        title: "儲存編輯資料成功!",
                        // text: "您的商品已成功下架.",
                        icon: "success",
                        confirmButtonColor: "#3085d6",
                      });
                    }
                  });
                }}
              >
                下架
              </button>
            </td>
          </tr>
        </tbody>
      </table>
      <div className="d-flex justify-content-center my-4">
        <button
          className="btn btn-secondary mx-2"
          onClick={() => {
            history.push("/machinemerchandise");
          }}
        >
          返回
        </button>
        <button className="btn btn-success text-white mx-2">確認</button>
      </div>
    </div>
  );
}

export default MachineMerchandiseEdit;
