import React, { useState } from "react";
import ReactDOM from 'react-dom';
// 引入元件
import { Drawer } from "antd";
import Swal from "sweetalert2";
import { HaderSelect } from "../ReactElement/InputCombo"
import { ColorBtn } from "../../components/ReactElement/ColorBtn";
import ReactBsModal from "react-bootstrap/Modal";
import { ReactBsFormGroup } from "../../../src/pages/Menber/Menber";
// 引入圖片
import cilUser from "../../images/svg/ui-icon/cil-user.svg";
import cilPeople from "../../images/svg/ui-icon/cil-people.svg";
import cilAccountLogout from "../../images/svg/ui-icon/cil-account-logout.svg";
import bellDot from "../../images/svg/ui-icon/bell-dot.svg";
// 引入函式
import { RemoveUser, RemoveSidebarOption,RemoveLabel} from "./RemoveLocalStorage"

// ==========通知按鈕==========
const CainBtn = () => {
  const [visible, setVisible] = useState(false);
  const showDrawer = () => {
    setVisible(true);
  };
  const onClose = () => {
    setVisible(false);
  };
  return <>
    <div type="primary" onClick={() => {
      if (document.body.clientWidth > 768) {
        Cain();
      }
      else {
        showDrawer();
        setTimeout(() => {
          let BellArrey = ["上架餐點審核通過", "結單時間通知", "機器已回報"];
          let BellOptionsLan = document.getElementsByClassName("BellOptionsLan")[1];
          let BellIcon = document.getElementsByClassName("cmsHeaderImg")[0];
          let BellOptionLists = BellArrey.map((BellArrey, i) => <h6 key={i} className="DrawerOption">{BellArrey}</h6>);
          if (BellArrey.length > 1) {
            BellIcon.src = "/static/media/bell-dot.cfcff14d.svg";
            ReactDOM.render(<>{BellOptionLists}</>, BellOptionsLan);
          } if (BellArrey.length === 1) {
            BellIcon.src = "/static/media/cil-bell.953a478d.svg"
            ReactDOM.render(<><h6 className="DrawerOption">{"無通知"}</h6></>, BellOptionsLan)
          }
        }, 100);
      }
    }} className="cmsHeaderIcon">
      <img className="cmsHeaderImg" src={bellDot} alt="" />
    </div>
    <Drawer
      bodyStyle={{
        backgroundColor: "white",
        padding: "0",
      }}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <h5 className="DrawerTitle">通知</h5>
      <span className="BellOptionsLan"></span>
    </Drawer>
  </>
}
// 因為DOM關係 要設時間
setTimeout(() => {
  if (window.location.href !== "http://localhost:3500/loginSelect" &&
    window.location.href !== "http://localhost:3500/menber" &&
    window.location.href !== "http://localhost:3500/Dashboard"
  ) {
    ReactDOM.render(<CainBtn />, document.getElementById('Cain'));
  }
}, 150);

// 帳號Icon下的選項(PC & RWD皆適用)
export function InvisibleOption() {
  return <>
    <h5 className="DrawerTitle">帳號</h5>
    <SelectCompanyId />
    <h6
      className="DrawerOption"
      onClick={() => {
        localStorage.setItem("urlLog", window.location.href, 6000)
        window.location.href = "http://localhost:3500/loginSelect"
      }}>
      <span>
        <img alt="" src={cilPeople}></img>
      </span>
      切換使用者身分
    </h6>
    <h6
      className="DrawerSignOut"
      onClick={() => {
        RemoveUser();
        RemoveSidebarOption();
        RemoveLabel();
        window.location.href = "http://localhost:3500/menber"
      }}>
      <span>
        <img alt="" src={cilAccountLogout}></img>
      </span>
      登出
    </h6>
  </>
}

// ==========帳號按鈕==========
const AbelBtn = () => {
  const [visible, setVisible] = useState(false);
  const showDrawer = () => {
    setVisible(true);
  };
  const onClose = () => {
    setVisible(false);
  };
  return <>
    <div type="primary" onClick={() => {
      if (document.body.clientWidth > 768) {
        Abel();
      }
      else {
        showDrawer();
      }
    }} className="cmsHeaderIcon">
      <img className="cmsHeaderImg" src={cilUser} alt="" />
      <span className="LoginName">{localStorage.LoginUser}</span>
    </div>
    <Drawer
      bodyStyle={{
        backgroundColor: "white",
        padding: "0",
      }}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <h5 className="DrawerTitle">切換店家</h5>
      <span className="DrawerSelect">
        <HaderSelect />
      </span>
      <InvisibleOption />
    </Drawer>
  </>
}
// 因為DOM關係 要設時間
setTimeout(() => {
  if (window.location.href !== "http://localhost:3500/loginSelect" &&
    window.location.href !== "http://localhost:3500/menber" &&
    window.location.href !== "http://localhost:3500/Dashboard") {
    ReactDOM.render(<AbelBtn />, document.getElementById('Abel'));
  }
}, 150);

//  點通知 or 帳號Icon 製造一個全螢幕的fake區塊
export function CainAbelNone() {
  return <div className="CainAbelNone" onClick={() => {
    if (CainShow === false) {
      Cain();
    }
    if (AbelShow === false) {
      Abel();
    }
  }}>點我就關閉ICONS</div>
}

// ===============點選右上ICON出現Option效果=============
let CainShow = true;
let AbelShow = true;
// 通知Icon 按下效果
export function Cain() {
  let TinkerbellOption = document.getElementsByClassName("TinkerbellOption")[0];
  let InvisibleOption = document.getElementsByClassName("InvisibleOption")[0];
  let CainAbelNone = document.getElementsByClassName("CainAbelNone")[0];
  CainShow = !CainShow;
  InvisibleOption.style.display = "none";
  CainAbelNone.style.display = CainShow ? "none" : "block";
  TinkerbellOption.style.display = CainShow ? "none" : "block";
}
// 通知Icon 按下效果
export function Abel() {
  let TinkerbellOption = document.getElementsByClassName("TinkerbellOption")[0];
  let InvisibleOption = document.getElementsByClassName("InvisibleOption")[0];
  let CainAbelNone = document.getElementsByClassName("CainAbelNone")[0];
  AbelShow = !AbelShow;
  TinkerbellOption.style.display = "none";
  CainAbelNone.style.display = AbelShow ? "none" : "block";
  InvisibleOption.style.display = AbelShow ? "none" : "block";
}

