import React, { useEffect } from "react";
// 輸入框&選擇框
import { LabelSelect, LabelInput } from "../../../components/ReactElement/InputCombo";
// 按鈕素體
import { ColorBtn, HoverBlackBtn } from "../../../components/ReactElement/ColorBtn";
import { PageBtnsHader, PageBtnsFooter } from "../../../components/ReactElement/OriginalLabel/LabelPageBtns"
// React BS Alert
import { ManagementBtn } from "./ManagementBtn";
// 表格
import { LabelTitleTwin } from "../../../components/ReactElement/OriginalLabel/LabelTitle"
import { LabelCheckerboard } from "../../../components/ReactElement/OriginalLabel/LabelCheckerboard"
// 引入表格需要的函示
import { ChangeWorld } from "./LabelFunction";
import { PageSelect } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function OverView() {
  let MenuDataTitle = ["商品名稱", "訂單編號", "BarCode", "價格", "主圖", "狀態", "操作"];
  localStorage.setItem("LabelTitleCost", MenuDataTitle.length, 60000) //製造表單標題格數
  if (localStorage.MaxPageValue === undefined) {
    localStorage.setItem("MaxPageValue", 10, 60000)
  }
  localStorage.setItem("LabelCotentCost", localStorage.MaxPageValue, 60000) //製造表單欄位格數 
  useEffect(() => {
    setTimeout(() => {
      ChangeWorld();
      CouncilBtn();
      PageSelect();
    }, 100);
  });
  return <>
    <div className="iunputCombo2">
      <div className="flexDiv">
        <LabelSelect p="分類" />
        <LabelSelect p="狀態" />
      </div>
      <div className="flexDiv">
        <LabelInput p="關鍵字查詢" placeholder="請輸入您的姓名" />
      </div>
      <span className="searchSpan">
        <ColorBtn
          className="SearchBtn"
          text="搜尋"
          backgroundColor="#20A8D8"
        />
      </span>
    </div>
    <div className="SearchAddBtns">
      <HoverBlackBtn
        className="SearchBtn"
        color="#2F353A"
        backgroundColor="white"
        border="1px solid #2F353A"
        marginRight="19px"
        text="匯出餐點"
      />
      <ManagementBtn
        className="SearchBtn"
        BtnColor="#2F353A"
        BtnBackgroundColor="white"
        BtnBorder="1px solid #2F353A"
        marginRight="10px"
        BtnTitle="分類管理"
      />
      <ColorBtn
        className="SearchBtn SearchBtnRWD"
        text="搜尋"
        backgroundColor="#1985AC"
      />
      <HoverBlackBtn
        className="RightBtn"
        border="none"
        color="white"
        text="新增品項"
        backgroundColor="#00BFA5"
        onClick={() => {
          window.location.href = "http://localhost:3500/menu/Add";
        }}
      />
    </div>
    < PageBtnsHader />
    <table className="ChienLiLabel">
      <LabelTitleTwin TitleText="水果" />
      <LabelCheckerboard />
    </table>
    <PageBtnsFooter />
  </>
}
//  ============商品是否有審核通過狀態顯示==========
function CouncilBtn() {
  let clickMeText0 = document.getElementsByClassName("clickMeText0")
  for (let index = 0; index < localStorage.MaxPageValue; index++) {
    if (clickMeText0[index].lastElementChild.textContent === "待審核") {
      clickMeText0[index].lastElementChild.style.backgroundColor = "#FFC107";
      clickMeText0[index].lastElementChild.style.color = "#4A3700";
      clickMeText0[index].lastElementChild.style.disply = "block";
    }
    else if (clickMeText0[index].lastElementChild.textContent === "審核失敗") {
      clickMeText0[index].lastElementChild.style.backgroundColor = "#F85756";
      clickMeText0[index].lastElementChild.style.color = "White";
      clickMeText0[index].lastElementChild.style.disply = "block";
    }
    else if (clickMeText0[index].lastElementChild.textContent === "審核通過") {
      clickMeText0[index].lastElementChild.style.disply = "none";
    }
  }
}
