import React from "react";
import { useHistory } from "react-router-dom";
import { storesDelectApi } from "../../middleware/api";
import Swal from "sweetalert2";
function StoreTopCompany({
  background,
  roomProperty,
  storeClickId,
  setStoreClickId,
  storesShowList,
  getStoreListAPI,
}) {
  const history = useHistory();
  const delStoresDelAPI = () => {
    storesDelectApi(storesShowList.id);
  };
  return (
    <div className="storeRightStore">
      <div className="d-flex flex-wrap">
        <div className="imgBox">
          <img src={background} alt="" />
        </div>
        <div className="storeRightStoreContext">
          <h4>{storesShowList && storesShowList.name}</h4>
          <p>{storesShowList && storesShowList.brief}</p>
          <div className="d-flex mt-3">
            {storesShowList &&
              storesShowList.attributes?.map((v, i) => {
                return (
                  <div className="storeRightRoomProperty" key={i}>
                    {v}
                  </div>
                );
              })}
          </div>
        </div>
      </div>
      <div>
        {storesShowList?.id ? (
          <button
            className="btn btn-primary m-2 text-white"
            onClick={() => {
              history.push(`/storeedit/${storesShowList.id}`);
            }}
          >
            編輯
          </button>
        ) : (
          <button className="btn btn-primary m-2 text-white">編輯</button>
        )}
        {storesShowList?.id ? (
          <button
            className="btn btn-danger m-2 text-white"
            onClick={() => {
              Swal.fire({
                title: "確認刪除?",
                text: "確認是否刪除，刪除後無法回復",
                icon: "warning",
                showCancelButton: true,
                confirmButtonColor: "#009E88",
                cancelButtonColor: "#C8CED3",
                confirmButtonText: "刪除",
                cancelButtonText: "取消",
                reverseButtons: true,
              }).then((result) => {
                if (result.isConfirmed) {
                  Swal.fire({
                    title: "刪除成功!",
                    text: "您的店家已成功刪除.",
                    icon: "success",
                    confirmButtonColor: "#3085d6",
                  });
                  delStoresDelAPI();
                  setTimeout(() => {
                    getStoreListAPI();
                  }, 1000);
                }
              });
            }}
          >
            刪除
          </button>
        ) : (
          <button className="btn btn-danger m-2 text-white">刪除</button>
        )}
      </div>
    </div>
  );
}

export default StoreTopCompany;
