import React from "react";

function Index() {
  localStorage.removeItem("SidebarIndex");
  localStorage.removeItem("OptionIndex");
  localStorage.setItem("LabelCotentCost", localStorage.MaxPageValue, 60000) //製造表單欄位格數 
  localStorage.removeItem("TrunPageMax");
  localStorage.removeItem("trIndex");
  localStorage.removeItem("LabelCotentCost");
  localStorage.removeItem("TurnPage");
  localStorage.removeItem("ShowManall");
  localStorage.removeItem("OptionIcons");
  return <div></div>;
}
export default Index;

// ======滾動滑鼠監聽事件======
window.addEventListener("scroll", () => {
  let scrollable = document.documentElement.scrollHeight - window.innerHeight;
  let scrolled = window.scrollY;
  if (window.location.href.split("http://localhost:3500/")[1] !== "menber") {
    if ((scrollable - scrolled) <= 50) {
      if (window.location.href !== "http://localhost:3500/loginSelect" &&
        window.location.href !== "http://localhost:3500/menber") {
        let context = document.getElementsByClassName("context")[0];
        context.className = "context haha";
      }
    }
    else {
      let context = document.getElementsByClassName("context")[0];
      if (window.location.href !== "http://localhost:3500/loginSelect" &&
        window.location.href !== "http://localhost:3500/menber") {
        context.className = "context";
      }
    }
  }
});
