import React, { useState } from "react";
let cost
// ====預設頁數按鈕多寡的值====
export function PageBtns() {
    let pageBtns = [];
    cost = Math.ceil(localStorage.LabelCotentCost / 10)
    //用迴圈將代辦事項的內容一個個放進空陣列中
    for (let i = 0; i <= cost - 1; i++) {
        //記得在JSX中使用JS變數要用花括號包著
        pageBtns.push(<button className="NumberBtn" key={i} value={i + 1} onClick={() => {
           if(2>1){
            localStorage.setItem("TurnPage", i, 60000);
           }
            TurnPage();
            window.location.reload();
        }}>{i + 1}</button>)
    }
    return (
        <span>
            {pageBtns}
        </span>
    )
}

// 表單上方顯示最多幾頁的選擇器
export function PageBtnsHader() {
    return <header className="showMaxPage">
        <h6>Show<span>
            <select className="MaxPage" onChange={() => {
                PageShowSelect();
                window.location.reload();
            }}>
                <option value={localStorage.MaxPageValue}>資料筆數</option>
                <option className="pageOptions" value="10">10</option>
                <option className="pageOptions" value="20">20</option>
                <option className="pageOptions" value="50">50</option>
                <option className="pageOptions" value="100">100</option>
            </select>
        </span>entries</h6>
    </header>
}

// 表單下方頁數按鈕與顯示多少頁數
export function PageBtnsFooter() {
    const [LabelData] = useState()
    if (2 < 1) {
        console.log(LabelData)
    }
    return <footer className="LabelFloor">
        <div className="HowManyPage">
            <h6>Showing
                <span className="pageNumber">1</span>
                to
                <span className="pageNumber"></span>
                of
                <span className="pageNumber"></span>
                entries</h6>
        </div>
        <div className="PageBtns">
            <button onClick={() => {
                if (localStorage.TurnPage <= 0) {
                    localStorage.setItem("TurnPage", 0, 60000);
                } else {
                    localStorage.setItem("TurnPage", parseInt(localStorage.TurnPage) - 1, 60000);
                }
                TurnPage();
            }}>Previous</button>
            <PageBtns />
            <button onClick={() => {
                if (localStorage.TurnPage < cost - 1) {
                    localStorage.setItem("TurnPage", parseInt(localStorage.TurnPage) + 1, 60000);
                }
                else {
                    localStorage.setItem("TurnPage", cost - 1, 60000);
                }
                TurnPage();
            }}>Next</button>
        </div>
    </footer>
}

// 頁數按鈕變更樣式
export function TurnPage() {
    let NumberBtn = document.getElementsByClassName("NumberBtn");
    for (let x = 0; x < NumberBtn.length; x++) {
        NumberBtn[x].style.backgroundColor = "white";
    }
    NumberBtn[localStorage.TurnPage].style.backgroundColor = "#F1F5FB";
}

export function PageShowSelect() {
    // ==============Label 依照顯示數值變更選單==============
    let MaxPage = document.getElementsByClassName("MaxPage")[0]; //10...50...100
    if (parseInt(localStorage.LabelCotentCost) > parseInt(MaxPage.value)) {
        localStorage.setItem("MaxPageValue", MaxPage.value, 600000)
    }
    else {
        localStorage.setItem("MaxPageValue", localStorage.LabelCotentCost, 600000)
    }
    let keyMan = document.getElementsByClassName("keyMan");
    let PageSelect = document.getElementsByClassName("pageNumber")[1];
    let Pagemax = document.getElementsByClassName("pageNumber")[2];
    let NumberBtn = document.getElementsByClassName("NumberBtn");
    for (let x = 0; x < keyMan.length; x++) {
        if (x < localStorage.MaxPageValue) {
            keyMan[x].style.display = "table-row";
            PageSelect.textContent = localStorage.MaxPageValue;
            Pagemax.textContent = x + 1;
        } else {
            keyMan[x].style.display = "none";
            PageSelect.textContent = localStorage.MaxPageValue;
            Pagemax.textContent = x + 1;
        }
        AddBtns(keyMan.length);
    }

    function AddBtns(x) {
        // =========頁數按鈕增減=========
        for (let y = 0; y < NumberBtn.length; y++) {
            if (Math.ceil(x / localStorage.MaxPageValue) > y) {
                NumberBtn[y].style.display = "initial"
                localStorage.setItem("TrunPageMax", y, 600000)
            }
            else {
                NumberBtn[y].style.display = "initial"
            }
        }
    }
}
