import React, { useState } from "react";
import ReactBsModal from "react-bootstrap/Modal";
import "../../styles/menber/loginSelect.css";
// 引入元件
import { ColorBtn } from "../../components/ReactElement/ColorBtn"
import { LabelSelect } from "../../components/ReactElement/InputCombo"
// 引入圖片
import MachineManagement from "../../images/svg/ui-icon/MachineManagement.svg"
import PopStore from "../../images/svg/ui-icon/PopStore.svg"
import Replenishment from "../../images/svg/ui-icon/Replenishment.svg"
import Admin from "../../images/svg/ui-icon/cil-user.svg"

async function IDLavel() {
    let userIdentity = {
        "user_identity": "1"
    }
    fetch("https://api-v1-t.hillcorp.com/cms2/account/choose-identity", {
        method: "POST",
        headers: {
            Accept: "application/json",
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.TK}`
        }, body: JSON.stringify(userIdentity)
    }).then(response => {
        return response.json()
    })
        .then(res => {
            console.log(res)
        })
}

export function LoginSelect() {
    if (window.location.href === "http://localhost:3500/loginSelect") {
        document.body.style.backgroundColor = "white";
        return <div className="WhatIdentity">
            <h1>選擇所屬身分</h1>
            <h6>ex-chienli.wang@hillcorp.com</h6>
            <div className="IdentityIcons">
                <SelectCompany onClick={(e) => {
                    IDLavel();
                }}
                    img={MachineManagement}
                    h5="機台管理"
                    h6="(智販/餐食)"
                >
                </SelectCompany>
                <SelectCompany onClick={(e) => {
                    IDLavel();
                }}
                    img={PopStore}
                    h5="POPGo!店家"
                >
                </SelectCompany>
                <SelectCompany onClick={(e) => {
                    IDLavel();
                }}
                    img={Replenishment}
                    h5="POPGo! 補貨員"
                >
                </SelectCompany>
            </div>
            <button onClick={(e) => {
                localStorage.removeItem("TK");
                window.location.href = "http://localhost:3500/menber";
            }} className="backBtn">返回登入頁</button>
            <button className="AdminBtn">系統管理員 <img alt="" src={Admin}></img></button>
        </div>
    }
}


//================選擇公司/店家視窗效果====================
export function SelectCompany(props) {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return <>
        <button
            className="IdentityIcon"
            variant="primary" onClick={handleShow}>
            <img alt="" src={props.img}></img>
            <h5>{props.h5}</h5>
            <h6>{props.h6}</h6>
        </button>
        <ReactBsModal show={show} onHide={handleClose}>
            <ReactBsModal.Header closeButton>
                <ReactBsModal.Title className="ModalTitle">選擇公司/店家</ReactBsModal.Title>
            </ReactBsModal.Header>
            <h5 className="SelectCompanyIllustrate">請選擇公司/店家進入該列表，或選擇新增公司/店家資料</h5>
            <div className="WhichStore">
                <LabelSelect p="選擇公司" />
            </div>
            <div className="WhichStore">
                <LabelSelect p="選擇店家" />
            </div>
            <hr className="WhichStoreHr" />
            <footer
                style={{
                    display: "flex",
                    paddingBottom: "40px"
                }}>
                <div className="backBtns"
                    style={{
                        marginTop: "-5px"
                    }}>
                    <ColorBtn className="BackBtn" text="返回" backgroundColor="#C8CED3" variant="primary" onClick={handleClose} />
                    <ColorBtn className="PassBtn" text="確認" color="white" backgroundColor="#00BFA5"
                        onClick={() => {
                            let WhichCompanyName = document.getElementsByClassName("SelectMe")[0].getElementsByTagName("select")[0].value;
                            let WhichStoreName = document.getElementsByClassName("SelectMe")[1].getElementsByTagName("select")[0].value;
                            if (WhichCompanyName !== "請選擇" && WhichStoreName !== "請選擇") {
                                if (localStorage.urlLog === undefined) {
                                    window.location.href = "http://localhost:3500/";
                                }
                                else {
                                    window.location.href = localStorage.urlLog;
                                    localStorage.removeItem("urlLog");
                                }

                            }
                        }} />
                </div>
            </footer>
        </ReactBsModal>
    </>
}
