import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Form, Row, Col } from "react-bootstrap";
import { Formik } from "formik";
import * as yup from "yup";
import TransferReact from "./TransferReact";
//測試文檔
const data = [
  {
    product: "甜食",
    productItem: [
      { key: "1", itemName: "巧克力" },
      { key: "2", itemName: "草莓" },
      { key: "3", itemName: "蛋糕" },
      { key: "4", itemName: "抹茶" },
      { key: "5", itemName: "千層派" },
      { key: "6", itemName: "香草冰淇淋" },
      { key: "7", itemName: "脆迪酥" },
      { key: "8", itemName: "大福" },
      { key: "9", itemName: "義每蛋捲" },
    ],
  },
  {
    product: "麵食",
    productItem: [
      { key: "1", itemName: "乾麵" },
      { key: "2", itemName: "炒麵" },
      { key: "3", itemName: "泡麵" },
      { key: "4", itemName: "餛飩麵" },
      { key: "5", itemName: "牛肉麵" },
      { key: "6", itemName: "蔥燒牛肉麵" },
      { key: "7", itemName: "乾拌麵" },
      { key: "8", itemName: "打拋豬肉麵" },
      { key: "9", itemName: "鍋燒意麵" },
    ],
  },
  {
    product: "飲料",
    productItem: [
      { key: "1", itemName: "紅茶" },
      { key: "2", itemName: "奶茶" },
      { key: "3", itemName: "珍珠奶茶" },
      { key: "4", itemName: "綠茶" },
      { key: "5", itemName: "鮮奶茶" },
      { key: "6", itemName: "冬瓜茶" },
      { key: "7", itemName: "檸檬冬瓜茶" },
      { key: "8", itemName: "蜂蜜檸檬茶" },
      { key: "9", itemName: "檸檬紅茶" },
    ],
  },
];

const children = [];
for (let i = 0; i < data.length; i++) {
  children.push(<option value={`${i}`}>{data[i].product}</option>);
}

const mockData = [];
for (let i = 0; i < 20; i++) {
  mockData.push({
    key: i.toString(),
    title: `content${i + 1}`,
    description: `description of content${i + 1}`,
  });
}
//初始在右側的值
// const initialTargetKeys = data
//   .filter((item) => +item.key > 10)
//   .map((item) => item.key);
//定義錯誤訊息及驗證
const schema = yup.object().shape({
  store: yup.string().required("請選擇店家"),
  activity_name: yup.string().required("請輸入活動名稱"),
  startDate: yup.string().required("請選擇開始日期"),
  endDate: yup.string().required("請選擇結束日期"),
  total_budget: yup.number().required("請填入預算"),
  // add_purchase: yup.string().required("請選擇加購商品"),
  // contact_name: yup.string().required("請輸入聯絡人姓名"),
  // contact_tel: yup
  //   .string()
  //   // eslint-disable-next-line no-useless-escape
  //   .matches(/09\d{2}\-?\d{3}\-?\d{3}/, "請輸入正確格式")
  //   .required("請輸入聯絡人電話號碼"),
  // contact_email: yup
  //   .string()
  //   .email("請輸入正確格式")
  //   .required("請輸入聯絡人信箱"),
});

function CombinationProduct({
  startADay,
  setStartADay,
  toDayDate,
  endADay,
  setEndADay,
  maxDay,
}) {
  const history = useHistory();
  const [allStageChecked, setAllStageChecked] = useState(false);
  //以下都是第一區穿梭框
  // const [targetKeys, setTargetKeys] = useState(initialTargetKeys);原始
  // const [targetKeys, setTargetKeys] = useState("");
  // const [selectedKeys, setSelectedKeys] = useState([]);
  const [productCategoryItemLeft, setProductCategoryItemLeft] = useState([]); //穿梭1
  const [productCategoryItemRight, setProductCategoryItemRight] = useState([]); //穿梭2

  // const onChange = (nextTargetKeys, direction, moveKeys) => {
  //   console.log("targetKeys:", nextTargetKeys);
  //   console.log("direction:", direction);
  //   console.log("moveKeys:", moveKeys);
  //   setTargetKeys(nextTargetKeys);
  // };

  // const onSelectChange = (sourceSelectedKeys, targetSelectedKeys) => {
  //   console.log("sourceSelectedKeys:", sourceSelectedKeys);
  //   console.log("targetSelectedKeys:", targetSelectedKeys);
  //   setSelectedKeys([...sourceSelectedKeys, ...targetSelectedKeys]);
  // };

  // const onScroll = (direction, e) => {
  //   console.log("direction:", direction);
  //   console.log("target:", e.target);
  // };
  //處理丟進穿梭框裡的數值
  const productCategoryHandle = (e) => {
    // console.log(e);
    const newValue = e.target.value;
    switch (e.target.name) {
      case "categoriesLeft":
        setProductCategoryItemLeft(data[newValue].productItem);
        break;

      default:
        setProductCategoryItemRight(data[newValue].productItem);
        break;
    }

    // setTargetKeys("");
  };

  //穿梭框二
  return (
    <Formik
      validationSchema={schema}
      onSubmit={(e) => {
        // setData([...data, e]);
        console.log(e);
      }}
      initialValues={{
        store: "",
        activity_name: "",
        startDate: "",
        endDate: "",
        total_budget: "",
        discounted_prices: "",
        // add_purchase: "",
      }}
    >
      {({ handleSubmit, handleChange, values, errors }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Group className="my-3 " as={Row} controlId="validationFormik03">
            <Form.Label column sm={2}>
              適用店家
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                as="select"
                placeholder="請選擇"
                name="store"
                value={allStageChecked ? (values.store = "All") : values.store}
                onChange={handleChange}
                isInvalid={!!errors.store}
                disabled={allStageChecked ? "disabled" : ""}
              >
                <option value="">請選擇</option>
                <option value="1">店家1</option>
                <option value="2">店家2</option>
                <option value="3">店家3</option>
              </Form.Control>
              <Form.Check
                type="checkbox"
                label="適用全平台"
                checked={allStageChecked}
                onChange={() => {
                  setAllStageChecked(!allStageChecked);
                }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.store}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>

          <Form.Group className="my-3" as={Row} controlId="validationFormik04">
            <Form.Label column sm={2}>
              活動名稱
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                placeholder="活動名稱"
                name="activity_name"
                value={values.activity_name}
                onChange={handleChange}
                isInvalid={!!errors.activity_name}
                maxLength="10"
              />
              <small style={{ color: "#768192" }}>
                EX:指令商品享加購折扣，全品項加購10元
              </small>
              <Form.Control.Feedback type="invalid">
                {errors.activity_name}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik05">
            <Form.Label column sm={2}>
              活動詳情
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                as="textarea"
                placeholder="請輸入內容"
                name="event_details"
                value={values.event_details}
                onChange={handleChange}
                isInvalid={!!errors.event_details}
              />
              <small style={{ color: "#768192" }}></small>
              <Form.Control.Feedback type="invalid">
                {errors.event_details}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik06">
            <Form.Label column sm={2}>
              日期起訖
            </Form.Label>
            <div className="col-sm-10 col-md-10 d-flex flex-wrap">
              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="startDate"
                  value={startADay}
                  onChange={(e) => {
                    handleChange(e);
                    setStartADay(e.target.value);
                  }}
                  isInvalid={!!errors.startDate}
                  min={toDayDate}
                />
                <small style={{ color: "#768192" }}>
                  行銷活動最長只能持續七天
                </small>
                <Form.Control.Feedback type="invalid">
                  {errors.startDate}
                </Form.Control.Feedback>
              </Col>
              <Form.Label
                column
                md={1}
                className="d-flex justify-content-center "
              >
                ~
              </Form.Label>

              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="endDate"
                  value={endADay}
                  onChange={(e) => {
                    handleChange(e);
                    setEndADay(e.target.value);
                  }}
                  isInvalid={!!errors.endDate}
                  min={endADay}
                  max={maxDay}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.endDate}
                </Form.Control.Feedback>
              </Col>
            </div>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik07">
            <Form.Label column sm={2}>
              總預算
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="number"
                // as="number"
                name="total_budget"
                value={values.total_budget}
                onChange={handleChange}
                isInvalid={!!errors.total_budget}
              />
              <Form.Control.Feedback type="invalid">
                {errors.total_budget}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik08">
            <Form.Label column sm={2}>
              組合商品
            </Form.Label>
            <Col sm={10} md={10}>
              <div className="CombinationProductSet">
                <div className="CombinationProductSetHeard">
                  <p>組合商品一</p>
                </div>
                <div className="CombinationProductSetBody">
                  <div className="CombinationProductSetBodyTitle">
                    <Form.Group
                      className="my-3 d-flex"
                      // as={Row}
                      controlId="validationFormik09"
                    >
                      <Form.Label column sm={2}>
                        組合優惠價格
                      </Form.Label>
                      <Col sm={8} md={3}>
                        <Form.Control
                          type="number"
                          // as="number"
                          name="discounted_prices"
                          value={values.discounted_prices}
                          onChange={handleChange}
                          isInvalid={!!errors.discounted_prices}
                        />
                        <Form.Control.Feedback type="invalid">
                          {errors.discounted_prices}
                        </Form.Control.Feedback>
                      </Col>
                    </Form.Group>
                    <div className="CombinationProductSetcontain  d-flex">
                      <div className="CombinationProductSetcontainHeard ">
                        <div className="CombinationProductSetcontainHeardTitle">
                          <p>第一區</p>
                        </div>
                        <div className="CombinationProductSetcontainBody">
                          <Form.Group
                            className="mb-3 "
                            as={Col}
                            controlId="validationFormik010"
                          >
                            <Form.Label column sm={3}>
                              商品分類
                            </Form.Label>
                            <Col sm={10} md={8}>
                              <Form.Control
                                as="select"
                                // defaultValue="請選擇"
                                placeholder="請選擇"
                                name="categoriesLeft"
                                value={values.categoriesLeft}
                                onChange={(e) => {
                                  productCategoryHandle(e);
                                  handleChange(e);
                                }}
                                isInvalid={!!errors.categoriesLeft}
                              >
                                <option value="5">請選擇</option>
                                {children}
                              </Form.Control>

                              <Form.Control.Feedback type="invalid">
                                {errors.categoriesLeft}
                              </Form.Control.Feedback>
                            </Col>
                          </Form.Group>
                          <TransferReact mockData={productCategoryItemLeft} />
                        </div>
                      </div>
                      <div className="CombinationProductSetcontainHeard ">
                        <div className="CombinationProductSetcontainHeardTitle">
                          <p>第二區</p>
                        </div>
                        <div className="CombinationProductSetcontainBody">
                          <Form.Group
                            className="mb-3 "
                            as={Col}
                            controlId="validationFormik22"
                          >
                            <Form.Label column sm={3}>
                              商品分類
                            </Form.Label>
                            <Col sm={10} md={8}>
                              <Form.Control
                                as="select"
                                // defaultValue="請選擇"
                                placeholder="請選擇"
                                name="categoriesRight"
                                value={values.categoriesRight}
                                onChange={(e) => {
                                  productCategoryHandle(e);
                                  handleChange(e);
                                }}
                                isInvalid={!!errors.categoriesRight}
                              >
                                <option value="">請選擇</option>
                                {children}
                                {/* {data.map((item, index) => {
                                  return (
                                    <option value={index}>
                                      {item.product}
                                    </option>
                                  );
                                })} */}
                              </Form.Control>

                              <Form.Control.Feedback type="invalid">
                                {errors.categoriesLeft}
                              </Form.Control.Feedback>
                            </Col>
                          </Form.Group>
                          <TransferReact mockData={productCategoryItemRight} />
                        </div>
                      </div>
                    </div>
                  </div>
                  <button className="storeCreatButtons mx-0 my-3">
                    新增組合商品
                  </button>
                </div>
              </div>
              {/* <Transfer
                dataSource={mockData}
                titles={["Source", "Target"]}
                targetKeys={targetKeys}
                selectedKeys={selectedKeys}
                onChange={onChange}
                onSelectChange={onSelectChange}
                onScroll={onScroll}
                render={(item) => item.title}
              /> */}
            </Col>
          </Form.Group>
          {/* 送出紐 */}
          <Form.Group
            as={Col}
            // md="1"
            controlId="validationFormikUsername"
            className="d-flex justify-content-center my-3"
          >
            <button
              type="submit"
              className="btn btn-light  mx-2"
              onClick={() => {
                history.push("marketing");
              }}
            >
              返回
            </button>
            <button type="submit" className="btn btn-success text-white mx-2">
              確認
            </button>
          </Form.Group>
        </Form>
      )}
    </Formik>
  );
}

