import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/Machine/Machine.scss";
import SwitchButton from "../ReactElement/SwitchButton";
import { ReactComponent as CilTrash } from "../../images/svg/ui-icon/cil-trash.svg";
import { ReactComponent as CilPlus } from "../../images/svg/ui-icon/cil-plus.svg";
//引入複數表單
// import MachineFrom from "../Machine/MachineFrom";
function MachineContrl({
  buttonTitle,
  buttonClassName,
  modalTitle,
  enterColor,
  closeColor,
  removeColor,
}) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [radioValue, setRadioValue] = useState({
    radioValueState: "",
  });
  const [time, setTime] = useState("");
  const handleChange = (e) => {
    console.log(e.target.value);
    if (e.target.value === radioValue.radioValueState) {
      setRadioValue({ radioValueState: "" });
    } else {
      setRadioValue({ radioValueState: e.target.value });
    }
  };

  //刪除單筆餐廳屬性

  //控制狀態
  // const stateHandler = () => {};

  return (
    <>
      <Button variant={buttonClassName} onClick={handleShow}>
        {buttonTitle}
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h4 style={{ fontWeight: "bold" }}>{modalTitle}</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="">
            {/* <MachineFrom /> */}
            <table className="table table-bordered text-center mb-3">
              <tbody>
                <tr style={{ backgroundColor: "#e6ebef" }}>
                  <th scope="col">壓縮機</th>
                  <th scope="col">風扇</th>
                  <th scope="col">補貨門</th>
                </tr>
                <tr>
                  <td>關閉</td>
                  <td>關閉</td>
                  <td>關閉</td>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="machineContrlBody">
            <div className="machineContrlBodyTitle">
              <p>殺菌燈設定</p>
            </div>
            <div className="machineContrlBodyBody">
              <div className="mcbGroup">
                <p>定時殺菌</p>
                <SwitchButton />
              </div>
              <div className="mcbGroup">
                <p>天數設定</p>

                <div className="mcbInputGroup">
                  <input
                    type="checkBox"
                    value="mcbEveryDay"
                    onChange={(e) => {
                      handleChange(e);
                    }}
                    checked={radioValue.radioValueState === "mcbEveryDay"}
                  />
                  <label>每日</label>
                </div>
                <div className="mcbInputGroup">
                  <input
                    type="checkBox"
                    value="mcbWorkDay"
                    onChange={(e) => {
                      handleChange(e);
                    }}
                    checked={radioValue.radioValueState === "mcbWorkDay"}
                  />
                  <label>平日</label>
                </div>
                <div className="mcbInputGroup">
                  <input
                    type="checkBox"
                    value="mcbWorkendDay"
                    onChange={(e) => {
                      handleChange(e);
                    }}
                    checked={radioValue.radioValueState === "mcbWorkendDay"}
                  />
                  <label>六日</label>
                </div>
              </div>
              <div className="mcbGroup">
                <p>持續時間</p>
                <div className="mcbInputGroup">
                  <input
                    type="checkBox"
                    value="mcbOneMinute"
                    onChange={(e) => {
                      if (time === "mcbOneMinute") {
                        setTime("");
                      } else {
                        setTime(e.target.value);
                      }
                    }}
                    checked={time === "mcbOneMinute"}
                  />
                  <label>1分鐘</label>
                </div>
                <div className="mcbInputGroup">
                  <input
                    type="checkBox"
                    value="mcbThreeMinute"
                    onChange={(e) => {
                      if (time === "mcbThreeMinute") {
                        setTime("");
                      } else {
                        setTime(e.target.value);
                      }
                    }}
                    checked={time === "mcbThreeMinute"}
                  />
                  <label>3分鐘</label>
                </div>
              </div>
            </div>
            <div className="machineContrlContent px-3">
              <table className="table table-bordered text-center mb-3">
                <tbody>
                  <tr>
                    <th>
                      <input type="checkBox" />
                    </th>
                    <th>執行時段</th>
                    <th>操作</th>
                  </tr>
                  <tr>
                    <td>
                      <input type="checkBox" />
                    </td>
                    <td>
                      <input className="componentsInput" type="date" />
                    </td>
                    <td>
                      <button
                        className="btn btn-danger p-1 mx-1"
                        style={{ width: "32px" }}
                      >
                        <CilTrash fill="#fff" />
                      </button>
                      <button
                        className="btn btn-success p-1 mx-1"
                        style={{ width: "32px" }}
                      >
                        <CilPlus fill="#fff" stroke="#fff" />
                      </button>
                      <div></div>
                    </td>
                  </tr>
                </tbody>
              </table>
              <div className="mcbGroup">
                <p>執行殺菌</p>
                <button className="btn btn-outline-dark me-2">1分鐘</button>
                <button className="btn btn-outline-dark">3分鐘</button>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant={removeColor} onClick={handleClose}>
            返回
          </Button>
          <Button variant={enterColor}>確認</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default MachineContrl;
