import React, { useEffect } from "react";
// CSS
import "../../../styles/OriginalLabel/OriginalLabel.css";
import "../../../styles/OriginalLabel/OriginalLabelCommon.css";
import "../../../styles/OriginalLabel/OriginalLabelProduct.css";
// import 'animate.css';
// 引入表格
import topArrew from "../../../images/svg/ui-icon/cil-arrow-top.svg"
import * as SalesReportData from "../../../components/Json/SalesReportData.json"
// 引入表格需要的函式
import { ChangeWorld } from "./LabelFunction"
//  引入大白按鈕
import { BigWhiteBtns } from "./BigWhiteBtns"
// 引入函式
import { TwinArrangementChange } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

// ===== 銷售報表上方表格標題 =========
export function LabelTitleDate() {
    let LabelTitleDates = [];
    for (let x = 0; x < 6; x++) {
        LabelTitleDates.push(
            <th className={"ChildTitle ChildTitle" + x}
                style={{
                    color: "#2F353A",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3"
                }} key={x}><span className="LabelTitleText"></span><img
                    className="arrangementTop"
                    alt=""
                    onClick={() => {
                        TwinArrangementChange(x);
                    }}
                    style={{
                        with: "3px",
                        height: "10px",
                        marginTop: "5px",
                        marginRight: "1px",
                        cursor: "pointer",
                        float: "right"
                    }}
                    src={topArrew}></img></th>
        )
    }
    return <thead>
        <tr>
            {LabelTitleDates}
        </tr>
    </thead>
}

// ===== 銷售報表下方表格標題 =========
export function LabelTitleProduct() {
    let LabelTitleProducts = [];
    for (let x = 0; x < 6; x++) {
        LabelTitleProducts.push(
            <th className={"ChildTitle ChildTitle" + x}
                style={{
                    color: "#2F353A",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3"
                }} key={x}><span className="TitleText"></span><img
                    className="arrTop"
                    alt=""
                    onClick={() => {
                        TwinArrangementChange(x);
                    }}
                    style={{
                        with: "3px",
                        height: "10px",
                        marginTop: "5px",
                        marginRight: "1px",
                        cursor: "pointer",
                        float: "right"
                    }}
                    src={topArrew}></img></th>
        )
    }
    return <thead>
        <tr>
            {LabelTitleProducts}
        </tr>
    </thead>
}

// ==============上方表格依照資料多寡製造出表格欄位================
export function TopLabelCheckerboard() {
    let LabelDatasTr = [];
    let ChildTd = [];
    let LabelDatasTd = [];
    // 表格標題(包含點擊+圖片)
    for (let x = 0; x < 6; x++) {
        LabelDatasTd.push(<td key={x} className={"TopChildTd TopChildTd" + x}>
            <span className={"TopclickMeSpan" + x}></span>
            <span className={"TopclickMeText" + x}></span>
        </td>)
    }
    // 手機螢幕所需要的表格
    for (let y = 3; y < 6; y++) {
        ChildTd.push(<div className="TopCellphoneLabel" key={y}>
            <span className={"TopChildGroupTitle TopChildGroupTitle" + y}></span>
            <span className={"TopChildGroupContent TopChildGroupContent" + y}></span>
        </div>)
    }
    // 表格欄位
    for (let z = 0; z < SalesReportData.DataComponet.length; z++) {
        LabelDatasTr.push(<tr key={z} className="TopKeyMan">{LabelDatasTd}</tr>)
        LabelDatasTr.push(<tr key={z + "tr"} className="TopChildGroup">
            <td>{ChildTd}</td>
        </tr>
        )
    }
    return <tbody>
        {LabelDatasTr}
    </tbody>
}
// ==============下方表格依照資料多寡製造出表格欄位================
export function BottomLabelCheckerboard() {
    let LabelDatasTr = [];
    let ChildTd = [];
    let LabelDatasTd = [];
    // 表格標題(包含點擊+圖片)
    for (let x = 0; x < 6; x++) {
        LabelDatasTd.push(<td key={x} className={"BottomChildTd BottomChildTd" + x}>
            <span className={"BottomclickMeSpan" + x}></span>
            <span className={"BottomclickMeText" + x}></span>
        </td>)
    }
    // 手機螢幕所需要的表格
    for (let y = 3; y < 6; y++) {
        ChildTd.push(<div className="BottomCellphoneLabel" key={y}>
            <span className={"BottomChildGroupTitle BottomChildGroupTitle" + y}></span>
            <span className={"BottomChildGroupContent BottomChildGroupContent" + y}></span>
        </div>)
    }
    // 表格欄位
    for (let z = 0; z < SalesReportData.DataComponet.length; z++) {
        LabelDatasTr.push(<tr key={z} className="BottomKeyMan">{LabelDatasTd}</tr>)
        LabelDatasTr.push(<tr key={z + "tr"} className="BottomChildGroup">
            <td>{ChildTd}</td>
        </tr>
        )
    }
    return <tbody>
        {LabelDatasTr}
    </tbody>
}

//   ===========銷售報表上方列表=============
export function SalesReport() {
    let SalesReportDataTitle = ["日期", "分店", "營業額", "折扣額", "實收金額", "操作"];
    localStorage.setItem("LabelTitleCost", SalesReportDataTitle.length, 60000) //製造表單標題格數
    if (localStorage.MaxPageValue === undefined) {
        localStorage.setItem("MaxPageValue", 10, 60000)
    }
    localStorage.setItem("LabelCotentCost", localStorage.MaxPageValue, 60000) //製造表單欄位格數 
    useEffect(() => {
        setTimeout(() => {
            ChangeWorld();
        }, 100);
    });
    return <>
        <BigWhiteBtns />
        <table className="ChienLiLabel">
            <LabelTitleDate />
            <TopLabelCheckerboard />
            <thead>
                <tr >
                    <th className="TotalRevenueTh">
                        <span className="TotalRevenueTitle">總營收：</span>
                        <span className="TotalRevenueText">$10,000</span>
                    </th>
                </tr>
            </thead>
        </table>
        <table style={{
            marginTop: "53px"
        }} className="ChienLiLabel">
            <LabelTitleProduct />
            <BottomLabelCheckerboard />
        </table>
    </>
}
