/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Form, Row, Col } from "react-bootstrap";
import { Formik } from "formik";
import * as yup from "yup";
// import moment from "moment";
import { Select } from "antd";
//定義錯誤訊息及驗證
const schema = yup.object().shape({
  store: yup.string().required("請選擇店家"),
  activity_name: yup.string().required("請輸入活動名稱"),
  startDate: yup.string().required("請選擇開始日期"),
  endDate: yup.string().required("請選擇結束日期"),
  discuoun_type: yup.string().required("請選擇折扣類別"),
  category: yup.string().required("請選擇折價類別"),
  discount_amount: yup.number().required("請輸入折扣金額"),
  total_budget: yup.number().required("請輸入總預算"),
});
const data = ["賣相", "龜祭", "五十嵐", "清新", "麥味登"];
//下拉多選
const { Option } = Select;
const children = [];
for (let i = 1; i < data.length; i++) {
  children.push(<Option key={data[i]}>{data[i]}</Option>);
}

function SingleProduct({
  startADay,
  setStartADay,
  toDayDate,
  endADay,
  setEndADay,
  maxDay,
}) {
  const [seleeec, setSeleeec] = useState([]);
  // const [startADay, setStartADay] = useState();
  // const [endADay, setEndADay] = useState();
  // const [maxDay, setMaxDay] = useState();
  const history = useHistory();
  const [allStageChecked, setAllStageChecked] = useState(false);
  function selecthandleChange(value) {
    console.log(`selected ${value}`);
    setSeleeec(value);
    console.log(seleeec);
  }
  // const resetEndTime = () => {
  //   console.log(startADay, endADay);
  //   if (!moment(startADay).isBefore(endADay)) {
  //     setEndADay(moment(startADay).add(1, "days").format("YYYY-MM-DD"));
  //   }
  // };
  //當前時間
  // const toDayDate = moment().startOf(String).format("YYYY-MM-DD");
  // 設定時間格式
  // useEffect(() => {
  //   resetEndTime();
  //   setEndADay(moment(startADay).add(1, "days").format("YYYY-MM-DD"));
  //   setMaxDay(moment(startADay).add(7, "days").format("YYYY-MM-DD"));
  // }, [startADay]);

  return (
    <Formik
      validationSchema={schema}
      onSubmit={(e) => {
        // setData([...data, e]);
        console.log(e);
      }}
      initialValues={{
        store: "",
        activity_name: "",
        event_details: "",
        startDate: "",
        endDate: "",
        discuoun_type: "",
        discount_amount: "",
        category: "category",
        selectValue: [],
      }}
    >
      {({ handleSubmit, handleChange, values, errors }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Group className="my-3 " as={Row} controlId="validationFormik03">
            <Form.Label column sm={2}>
              適用店家
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                as="select"
                placeholder="請選擇"
                name="store"
                value={allStageChecked ? (values.store = "All") : values.store}
                onChange={handleChange}
                isInvalid={!!errors.store}
                disabled={allStageChecked ? "disabled" : ""}
              >
                <option value="">請選擇</option>
                <option value="1">店家1</option>
                <option value="2">店家2</option>
                <option value="3">店家3</option>
              </Form.Control>
              <Form.Check
                type="checkbox"
                label="適用全平台"
                checked={allStageChecked}
                onChange={() => {
                  setAllStageChecked(!allStageChecked);
                }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.store}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik04">
            <Form.Label column sm={2}>
              活動名稱
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                placeholder="活動名稱"
                name="activity_name"
                value={values.activity_name}
                onChange={handleChange}
                isInvalid={!!errors.activity_name}
                maxLength="50"
              />
              <small style={{ color: "#768192" }}>EX:指定商品折價10元</small>
              <Form.Control.Feedback type="invalid">
                {errors.activity_name}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik05">
            <Form.Label column sm={2}>
              活動詳情
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                as="textarea"
                placeholder="請輸入內容"
                name="event_details"
                value={values.event_details}
                onChange={handleChange}
                isInvalid={!!errors.event_details}
                maxLength="300"
              />
              <small style={{ color: "#768192" }}></small>
              <Form.Control.Feedback type="invalid">
                {errors.event_details}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik06">
            <Form.Label column sm={2}>
              日期起訖
            </Form.Label>
            <div className="col-sm-10 col-md-10 d-flex flex-wrap">
              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="startDate"
                  value={startADay}
                  onChange={(e) => {
                    handleChange(e);
                    setStartADay(e.target.value);
                  }}
                  isInvalid={!!errors.startDate}
                  min={toDayDate}
                />
                <small style={{ color: "#768192" }}>
                  行銷活動最長只能持續七天
                </small>
                <Form.Control.Feedback type="invalid">
                  {errors.startDate}
                </Form.Control.Feedback>
              </Col>
              <Form.Label
                column
                md={1}
                className="d-flex justify-content-center "
              >
                ~
              </Form.Label>

              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="endDate"
                  value={endADay}
                  onChange={(e) => {
                    handleChange(e);
                    setEndADay(e.target.value);
                  }}
                  isInvalid={!!errors.endDate}
                  min={startADay}
                  max={maxDay}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.endDate}
                </Form.Control.Feedback>
              </Col>
            </div>
          </Form.Group>
          <Form.Group
            className="my-3 align-items-center"
            as={Row}
            controlId="validationFormik13"
          >
            <Form.Label column sm={2}>
              折價類別
            </Form.Label>
            <Col sm={10} md={6}>
              <div
                className="d-flex pt-1 "
                onChange={handleChange}
                value={values.category}
              >
                <Form.Check
                  className="me-3"
                  name="category"
                  type="radio"
                  value="category"
                  label="指定商品"
                  id="category"
                  defaultChecked={values.category === "category"}
                />
                <Form.Check
                  className="me-3"
                  name="category"
                  type="radio"
                  value="allCategory"
                  label="整個類別"
                  id="allCategory"
                  defaultChecked={values.category === "allCategory"}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.category}
                </Form.Control.Feedback>
              </div>
              <Select
                // value={values.selectValue}
                name="selectValue"
                mode="multiple"
                allowClear
                style={{ width: "100%" }}
                placeholder="請選擇商品"
                // defaultValue={["a10", "c12"]}
                // onChange={handleChange}
                onChange={selecthandleChange}
              >
                {children}
              </Select>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik07">
            <Form.Label column sm={2}>
              折扣
            </Form.Label>
            <Col sm={10} md={6}>
              <div className="d-flex  ">
                <div className="input-group  mb-3">
                  <Col>
                    <Form.Control
                      as="select"
                      placeholder="請選擇"
                      name="discuoun_type"
                      value={values.discuoun_type}
                      onChange={handleChange}
                      isInvalid={!!errors.discuoun_type}
                    >
                      <option value="">請選擇</option>
                      <option value="固定折價">固定折價</option>
                      <option value="百分比折價">百分比折價</option>
                    </Form.Control>
                    <Form.Control.Feedback type="invalid">
                      {errors.discuoun_type}
                    </Form.Control.Feedback>
                  </Col>
                  <Col>
                    <Form.Control
                      type="number"
                      name="discount_amount"
                      value={values.discount_amount}
                      onChange={handleChange}
                      isInvalid={!!errors.discount_amount}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors.discount_amount}
                    </Form.Control.Feedback>
                  </Col>
                  <span className="input-group-text" style={{ height: "38px" }}>
                    {values.discuoun_type === "百分比折價" ? "折" : "元"}
                  </span>
                </div>
              </div>
            </Col>
          </Form.Group>

          <Form.Group className="my-3" as={Row} controlId="validationFormik08">
            <Form.Label column sm={2}>
              總預算
            </Form.Label>
            <Col sm={5} md={3}>
              <Form.Control
                type="number"
                // as="number"
                name="total_budget"
                value={values.total_budget}
                onChange={handleChange}
                isInvalid={!!errors.total_budget}
              />
              <Form.Control.Feedback type="invalid">
                {errors.total_budget}
              </Form.Control.Feedback>
            </Col>
            <div className="col-md-3 col-sm-5" style={{ lineHeight: "38px" }}>
              元
            </div>
          </Form.Group>
          {/* 送出紐 */}
          <Form.Group
            as={Col}
            controlId="validationFormikUsername"
            className="d-flex justify-content-center my-3"
          >
            <button
              type="submit"
              className="btn btn-light  mx-2"
              onClick={() => {
                history.push("marketing");
              }}
            >
              返回
            </button>
            <button type="submit" className="btn btn-success text-white mx-2">
              確認
            </button>
          </Form.Group>
        </Form>
      )}
    </Formik>
  );
}

export default SingleProduct;
