import React, { useState } from "react";
import { Form, Row, Col } from "react-bootstrap";
import { Formik } from "formik";
import * as yup from "yup";
import { ReactComponent as CilPlus } from "../../../images/svg/ui-icon/cil-plus.svg";
import { ReactComponent as CilTrash } from "../../../images/svg/ui-icon/cil-trash.svg";
//定義錯誤訊息及驗證
const schema = yup.object().shape({
  contact_name: yup.string().required("請輸入聯絡人姓名"),
  contact_tel: yup
    .string()
    // eslint-disable-next-line no-useless-escape
    .matches(/09\d{2}\-?\d{3}\-?\d{3}/, "請輸入正確格式")
    .required("請輸入聯絡人電話號碼"),
  contact_email: yup
    .string()
    .email("請輸入正確格式")
    .required("請輸入聯絡人信箱"),
});
function MachineSales({ title, tooltip, telSpan, emailSpan }) {
  const [data, setData] = useState([]);
  return (
    <>
      <div className="ml-mx-5 mb-4">
        <div className="machineContrlBody">
          <div className="machineContrlBodyTitle d-flex align-items-center">
            <p>{title}</p>
            {tooltip ? (
              <button
                type="button"
                className="btn btnTooltips mt-1"
                data-bs-toggle="tooltip"
                data-bs-placement="top"
                title={tooltip}
                style={{ width: "10px" }}
              >
                ?
              </button>
            ) : (
              ""
            )}
          </div>
          <div className="machineContrlBodyBody">
            {data.map((v, i) => {
              return (
                <div key={i} className="d-flex flex-wrap">
                  <div className="flex-fill">
                    <Row>
                      <Form.Group className="my-1" as={Col} lg="4">
                        <Form.Control
                          type="text"
                          defaultValue={v.contact_name}
                        />
                      </Form.Group>
                      <Form.Group className="my-1" as={Col} lg="4">
                        <Form.Control
                          className="my-1"
                          lg="4"
                          type="text"
                          defaultValue={v.contact_tel}
                        />
                        <small style={{ color: "#768192" }}>{telSpan}</small>
                      </Form.Group>
                      <Form.Group className="my-1" as={Col} lg="4">
                        <Form.Control
                          className="my-1"
                          lg="4"
                          type="text"
                          defaultValue={v.contact_email}
                        />
                        <small style={{ color: "#768192" }}>{emailSpan}</small>
                      </Form.Group>
                    </Row>
                  </div>
                  <div className="MachineNotificationBtn">
                    <Form.Group
                      as={Col}
                      md="1"
                      controlId="validationFormikUsername"
                    >
                      <button type="submit" className="btn btn-danger mb-4">
                        <CilTrash fill="#fff" />
                      </button>
                    </Form.Group>
                  </div>
                </div>
              );
            })}
            <Formik
              validationSchema={schema}
              onSubmit={(e) => {
                setData([...data, e]);
                console.log(data);
              }}
              initialValues={{
                contact_name: "",
                contact_tel: "",
                contact_email: "",
              }}
            >
              {({ handleSubmit, handleChange, values, errors }) => (
                <Form noValidate onSubmit={handleSubmit}>
                  <div className="d-flex flex-wrap">
                    <div className="flex-fill">
                      <Row className="mb-3">
                        <Form.Group
                          className="my-1"
                          as={Col}
                          lg="4"
                          controlId="validationFormik01"
                        >
                          <Form.Control
                            type="text"
                            placeholder="請輸入聯絡人姓名"
                            name="contact_name"
                            value={values.contact_name}
                            onChange={handleChange}
                            isInvalid={!!errors.contact_name}
                          />
                          <Form.Control.Feedback type="invalid">
                            {errors.contact_name}
                          </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group
                          className="my-1"
                          as={Col}
                          lg="4"
                          controlId="validationFormik02"
                        >
                          <Form.Control
                            type="text"
                            placeholder="請輸入電話號碼"
                            name="contact_tel"
                            value={values.contact_tel}
                            onChange={handleChange}
                            isInvalid={!!errors.contact_tel}
                            maxLength="10"
                          />
                          <small style={{ color: "#768192" }}>{telSpan}</small>
                          <Form.Control.Feedback type="invalid">
                            {errors.contact_tel}
                          </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group
                          className="my-1"
                          as={Col}
                          lg="4"
                          controlId="validationFormikUsername"
                        >
                          <Form.Control
                            type="text"
                            placeholder="請輸入Email"
                            name="contact_email"
                            value={values.contact_email}
                            onChange={handleChange}
                            isInvalid={!!errors.contact_email}
                          />
                          <small style={{ color: "#768192" }}>
                            {emailSpan}
                          </small>
                          <Form.Control.Feedback type="invalid">
                            {errors.contact_email}
                          </Form.Control.Feedback>
                        </Form.Group>
                      </Row>
                    </div>
                    <div className="MachineNotificationBtn">
                      <Form.Group
                        as={Col}
                        md="1"
                        controlId="validationFormikUsername"
                      >
                        <button type="submit" className="btn btn-success ">
                          <CilPlus fill="#fff" />
                        </button>
                      </Form.Group>
                    </div>
                  </div>
                </Form>
              )}
            </Formik>
          </div>
        </div>
      </div>
    </>
  );
}

export default MachineSales;
