import axios from "axios";


// startTime: '2022-03-14 00:00:00', endTime: '2022-03-14 06:00:00'
// startTime: '2022-03-14 06:00:00', endTime: '2022-03-14 18:00:00'
// startTime: '2022-03-14 18:00:00', endTime: '2022-03-15 06:00:00'


let WeatherRequest = axios.create({
  baseURL: "https://opendata.cwb.gov.tw/api/v1/rest/datastore/"
});
//====台灣中央氣象局api=======
export let WeatherApi = (Area, DayEnd) =>
  WeatherRequest.get(Area + "?Authorization=CWB-A0E4A14B-FEB4-49ED-8473-4DD8300A8518" +
    "&timeFrom=" + DayEnd + "T18:00:00"
    +
    "&timeTo=" + DayEnd + "T23:00:00");
