//  ==================================web 只有一種表格 手機螢幕時，點選 + 按鈕展開表單的欄位 ==================================
let isShow = true;
export function UnfoldTd(trIndex) {
  let ClickMe = document.getElementsByClassName("ClickMe");
  let ChildGroup = document.getElementsByClassName("ChildGroup");
  let keyMan = document.getElementsByClassName("keyMan");
  let ChildTd0 = document.getElementsByClassName("ChildTd0");
  let ChildTd1 = document.getElementsByClassName("ChildTd1");
  let ChildTd2 = document.getElementsByClassName("ChildTd2");
  let LabelFloor = document.getElementsByClassName("LabelFloor")[0];
  for (let i = 0; i < ClickMe.length; i++) {
    ClickMe[i].style.backgroundColor = "#20A8D8";
    ClickMe[i].style.lineHeight = "20px";
    ClickMe[i].innerHTML = "+";
    ChildGroup[i].style.display = "none";
  }
  isShow = !isShow;
  ClickMe[trIndex].style.backgroundColor = isShow ? "#20A8D8" : "#F85756";
  ClickMe[trIndex].style.lineHeight = isShow ? "20px" : "16px";
  ClickMe[trIndex].innerHTML = isShow ? "+" : "-";
  ChildGroup[trIndex].style.display = isShow ? "none" : "block";
  if (trIndex < parseInt(localStorage.MaxPageValue) - 1) {
    keyMan[trIndex + 1].style.height = isShow ? "39px" :
      (ChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + 39 + "px";
    ChildTd0[trIndex + 1].style.paddingTop = isShow ? "8px" :
      (ChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
    ChildTd1[trIndex + 1].style.paddingTop = isShow ? "8px" :
      (ChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
    ChildTd2[trIndex + 1].style.paddingTop = isShow ? "8px" :
      (ChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
  }
  if (trIndex === parseInt(localStorage.MaxPageValue) - 1) {
    LabelFloor.style.paddingTop = isShow ? "20px" :
      (ChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
  }
}

//  ==================================web 有上下表格 手機螢幕時，點選 + 按鈕展開上方表單的欄位 ==================================
let isShowTop = true;
export function UnfoldTdTop(trIndex) {
  let TopClickMe = document.getElementsByClassName("TopClickMe");
  let TopChildGroup = document.getElementsByClassName("TopChildGroup");
  let keyMan = document.getElementsByClassName("TopKeyMan");
  let ChildTd0 = document.getElementsByClassName("TopChildTd0");
  let ChildTd1 = document.getElementsByClassName("TopChildTd1");
  let ChildTd2 = document.getElementsByClassName("TopChildTd2");
  let TotalRevenueTh = document.getElementsByClassName("TotalRevenueTh")[0];
  let ChienLiLabel = document.getElementsByClassName("ChienLiLabel");
  for (let i = 0; i < TopClickMe.length; i++) {
    TopClickMe[i].style.backgroundColor = "#20A8D8";
    TopClickMe[i].style.lineHeight = "20px";
    TopClickMe[i].innerHTML = "+";
    TopChildGroup[i].style.display = "none";
  }
  isShowTop = !isShowTop;
  TopClickMe[trIndex].style.backgroundColor = isShowTop ? "#20A8D8" : "#F85756";
  TopClickMe[trIndex].style.lineHeight = isShowTop ? "20px" : "17px";
  TopClickMe[trIndex].innerHTML = isShowTop ? "+" : "-";
  TopChildGroup[trIndex].style.display = isShowTop ? "none" : "block";
  if (trIndex < parseInt(localStorage.MaxPageValue) - 1) {
    keyMan[trIndex + 1].style.height = isShowTop ? "39px" :
      (TopChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + 39 + "px";
    ChildTd0[trIndex + 1].style.paddingTop = isShowTop ? "8px" :
      (TopChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
    ChildTd1[trIndex + 1].style.paddingTop = isShowTop ? "8px" :
      (TopChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
    ChildTd2[trIndex + 1].style.paddingTop = isShowTop ? "8px" :
      (TopChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
  }
  if (trIndex === parseInt(localStorage.MaxPageValue) - 1) {
    TotalRevenueTh.style.marginTop = isShowTop ? "0px" :
      (TopChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) -6 + "px";
    ChienLiLabel[1].style.marginTop = isShowTop ? "53px" :
      (TopChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + 48 + "px";
  }
}
//  ==================================web 有上下表格手機螢幕時，點選 + 按鈕展開下方表單的欄位 ==================================
let isShowBottom = true;
export function UnfoldTdBottom(trIndex) {
  let BottomClickMe = document.getElementsByClassName("BottomClickMe");
  let BottomChildGroup = document.getElementsByClassName("BottomChildGroup");
  let keyMan = document.getElementsByClassName("BottomKeyMan");
  let ChildTd0 = document.getElementsByClassName("BottomChildTd0");
  let ChildTd1 = document.getElementsByClassName("BottomChildTd1");
  let ChildTd2 = document.getElementsByClassName("BottomChildTd2");
  for (let i = 0; i < BottomClickMe.length; i++) {
    BottomClickMe[i].style.backgroundColor = "#20A8D8";
    BottomClickMe[i].style.lineHeight = "20px";
    BottomClickMe[i].innerHTML = "+";
    BottomChildGroup[i].style.display = "none";
  }
  isShowBottom = !isShowBottom;
  BottomClickMe[trIndex].style.backgroundColor = isShowBottom ? "#20A8D8" : "#F85756";
  BottomClickMe[trIndex].style.lineHeight = isShowBottom ? "20px" : "17px";
  BottomClickMe[trIndex].innerHTML = isShowBottom ? "+" : "-";
  BottomChildGroup[trIndex].style.display = isShowBottom ? "none" : "block";
  if (trIndex < parseInt(localStorage.MaxPageValue) - 1) {
    keyMan[trIndex + 1].style.height = isShowBottom ? "39px" :
      (BottomChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + 39 + "px";
    ChildTd0[trIndex + 1].style.paddingTop = isShowBottom ? "8px" :
      (BottomChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
    ChildTd1[trIndex + 1].style.paddingTop = isShowBottom ? "8px" :
      (BottomChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
    ChildTd2[trIndex + 1].style.paddingTop = isShowBottom ? "8px" :
      (BottomChildGroup[trIndex].getElementsByTagName("td")[0].getElementsByTagName("div").length * 50) + "px";
  }
}

// ==================================表格標題旁的排序箭頭效果===================================================
let isTop = true;
export function ArrangementChange(arrangement) {

  let arrangementTop = document.getElementsByClassName("arrangementTop");
  isTop = !isTop
  for (let x = 0; x < arrangementTop.length; x++) {
    arrangementTop[x].style.transform = "rotate(" + 0 + "deg)";
  }
  arrangementTop[arrangement].style.transform = isTop ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
}

let isTwinTop = true;
let isTwinBottom = true;
//  ==================================web 有上下表格  表格標題旁的排序箭頭效果 ==================================
export function TwinArrangementChange(arrangement) {
  let arrangementTop = document.getElementsByClassName("arrangementTop");
  let arrTop = document.getElementsByClassName("arrTop");
  // 上方表格的箭頭效果
  isTwinTop = !isTwinTop
  for (let x = 0; x < arrangementTop.length; x++) {
    arrangementTop[x].style.transform = "rotate(" + 0 + "deg)";
  }
  arrangementTop[arrangement].style.transform = isTwinTop ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
  // 下方表格的箭頭效果
  isTwinBottom = !isTwinBottom
  for (let y = 0; y < arrangementTop.length; y++) {
    arrTop[y].style.transform = "rotate(" + 0 + "deg)";
  }
  arrTop[arrangement].style.transform = isTwinBottom ? "rotate(" + 0 + "deg)" : "rotate(" + 180 + "deg)";
}

// ==================================Label預設顯示多少筆資料 以及頁數按鈕===================================================
export function PageSelect() {
  let keyMan = document.getElementsByClassName("keyMan");
  let PageSelect = document.getElementsByClassName("pageNumber")[1];
  let Pagemax = document.getElementsByClassName("pageNumber")[2];
  let NumberBtn = document.getElementsByClassName("NumberBtn");
  if (localStorage.TurnPage === undefined) {
    localStorage.setItem("TurnPage", 0, 60000)
  }
  for (let x = 0; x < keyMan.length; x++) {
    if (x >= parseInt(localStorage.MaxPageValue))
      keyMan[x].style.display = "none";
    PageSelect.innerHTML = localStorage.MaxPageValue;
    Pagemax.innerHTML = x + 1
    NumberBtn[localStorage.TurnPage].style.backgroundColor = "#F1F5FB";
  }
  // 資料少於10筆將顯示幾筆資料的區塊全部隱藏
  let showMaxPage = document.getElementsByClassName("showMaxPage")[0];
  let LabelFloor = document.getElementsByClassName("LabelFloor")[0];
  if (localStorage.LabelCotentCost < 10) {
    showMaxPage.style.display = "none";
    LabelFloor.style.display = "none";
  }
  else {
    showMaxPage.style.display = "block";
    LabelFloor.style.display = "-webkit-inline-box";
  }
}
