import React from "react";
import { AssessBtn } from "./ReactBsAlert"
import "../../styles/ReactElement/ReactElement.css";
// 引用圖片
import dots from "../../images/images/gif/dots.gif"

// 普通彩色按鈕
export function ColorBtn(props) {
    return <button
        style={{
            width: "52px",
            height: "35px",
            color: "white",
            backgroundColor: props.backgroundColor,
            borderRadius: "4px",
            border: "none",
            fontSize: "14px",
            lineHeight: "29px",
            marginLeft: props.marginLeft
        }}
        onClick={props.onClick}
        variant={props.variant}
        className={props.className}>
        {props.text}
    </button>
}

// 白底按鈕
export function BackgroundWhiteBtn(props) {
    return <button
        style={{
            width: "52px",
            height: "35px",
            margin:"0 3px",
            borderRadius: "4px",
            fontSize: "14px",
            lineHeight: "29px",
            marginLeft: props.marginLeft
        }}
        onClick={props.onClick}
        variant={props.variant}
        className={props.className}>
        {props.text}
    </button>
}

// 長型彩色按鈕
export function LongColorBtn(props) {
    return <button
        style={{
            width: "120px",
            height: "35px",
            lineHeight: "25px",
            color: props.color,
            backgroundColor: props.backgroundColor,
            borderRadius: "4px",
            border: "none",
            fontSize: "14px",
            fontStyle: "Source Sans Pro",
            marginLeft: props.marginLeft
        }}
        onClick={props.onClick}
        variant={props.variant}
        className={props.className}>
        {props.text}
    </button>
}

// 鼠標滑入滑出長形按鈕
export function HoverBlackBtn(props) {
    return <button
        style={{
            marginRight: props.marginRight,
            backgroundColor: props.backgroundColor,
            color: props.color,
            border: props.border,
            fontSize: "14px",
            height: "35px",
            width: "80px",
            lineHeight: "32px",
            borderRadius: "4px",
        }}
        className={props.className}
        onClick={props.onClick}>
        {props.text}
    </button>
}

//  + 展開按鈕
export function ClickMe(props) {
    return <span
        style={{
            marginLeft: props.marginLeft
        }}
        className={"ClickMe " + props.className}
        onClick={props.onClick}>+</span>
}

// <編輯> & <刪除> 按鈕組合
export function TwinBtns() {
    return <span className="OperateBtns">
        <ColorBtn className="EditBtn" text="編輯" backgroundColor="#20A8D8" />
        <ColorBtn className="DeleteBtn" text="刪除" backgroundColor="#F85756" />
    </span>
}

// RWD表單 <編輯> & <刪除> 按鈕組合
export function ChildTwinBtns() {
    return <span className="ChildOperateBtns">
        <ColorBtn className="EditBtn" text="編輯" backgroundColor="#20A8D8" />
        <ColorBtn className="DeleteBtn" text="刪除" backgroundColor="#F85756" />
    </span>
}

//<餐點審核>  <編輯> & <刪除> 按鈕組合
export function TripletsBtns(props) {
    return <span className="OperateBtns">
        <AssessBtn BtnBackgroundColor="#FFC107" BtnColor="#4F5D73"
            BtnTitle="餐點審核"
            type={props.type}
            text={props.text}
            indivoduce={props.indivoduce}
            Amount={props.Amount}
            pic={props.pic}
        />
        <ColorBtn className="EditBtn" text="編輯" backgroundColor="#20A8D8" />
        <ColorBtn className="DeleteBtn" text="刪除" backgroundColor="#F85756" />
    </span>
}

//RWD表單 <餐點審核>  <編輯> & <刪除> 按鈕組合
export function ChildTripletsBtns(props) {
    return <span className="ChildOperateBtns">
        <AssessBtn BtnBackgroundColor="#FFC107" BtnColor="#4F5D73"
            BtnTitle="餐點審核"
            type={props.type}
            text={props.text}
            indivoduce={props.indivoduce}
            Amount={props.Amount}
            pic={props.pic}
        />
        <ColorBtn className="EditBtn" text="編輯" backgroundColor="#20A8D8" />
        <ColorBtn className="DeleteBtn" text="刪除" backgroundColor="#F85756" />
    </span>
}

// 對帳單PDF按鈕 & 補貨取餐明細EXL按鈕
export function ReconciliationBtns() {
    return <span className="OperateBtns">
        <button className="ReconciliationBtn">對帳單PDF</button>
        <button className="DetailBtn">補貨取餐明細EXCEL</button>
    </span>
}

// RWD 對帳單PDF按鈕 & 補貨取餐明細EXL按鈕
export function ChildReconciliationBtns() {
    return <span className="ChildOperateBtns">
        <button className="ReconciliationBtn">對帳單PDF</button>
        <button className="DetailBtn">補貨取餐明細EXCEL</button>
    </span>
}

// 超大白色包覆SVG以及文字的按鈕方框
export function BigWhiteBtn(props) {
    return <div className="BigWhiteBtn">
        <div className="BigWhiteIcon">
            {props.icon}
        </div>
        <div className="BigWhiteTexts">
            <h6>{props.title}</h6>
            <h4>{props.amount}</h4>
        </div>
        <div
            onMouseMove={props.onMouseMove}
            onMouseOut={props.onMouseOut}
            onClick={props.onClick}
            className="BigWhiteMore">
            <div className="BtnDot"></div>
            <div className="BtnDot"></div>
            <div className="BtnDot"></div>
            <img className="BtnDotJump" alt="" src={dots} />
        </div>
    </div>
}

// 詳細  按鈕
export function DetailedBtn() {
    return <button className="DetailedBtn">
        詳細
    </button>
}
