import ReactDOM from 'react-dom';
import dayjs from 'dayjs';
import { WeatherApi } from "../../api/api";
import "../../styles/work/weather.css";
import { WhatDates } from "./weatherFunction";
import sun from "../../images/png/sun.png";
import mountains from "../../images/png/mountains.png";

let Today = new Date();
let TimeFrom = dayjs(Today).format('YYYY-MM-DD') + "T06:00:00";
let TimeTo = dayjs(Today).format('YYYY-MM-DD') + "T18:00:00";

export function Day12Hrs(day) {
    if (dayjs(day).format('HH') < "18") {
        TimeFrom = dayjs(day).format('YYYY-MM-DD') + "T06:00:00";
        TimeTo = dayjs(day).format('YYYY-MM-DD') + "T18:00:00";
    }
    else if (dayjs(day).format('HH') > "18") {
        TimeFrom = dayjs(day).format('YYYY-MM-DD') + "T18:00:00";
        TimeTo = dayjs(day).add(1, 'day').format('YYYY-MM-DD') + "T06:00:00";
    }
    // WeatherMap();
}

// ===============  氣象紀錄 ==================
// 晴時多雲 多雲時陰短暫雨 陰有雨
// 稍有寒意至舒適 寒冷

// ==============天氣區塊======================
let WeatherMap = async () => {
    let SelectArea = document.getElementsByClassName("SelectArea")[0];
    try {
        let Item = await WeatherApi(document.getElementsByClassName("SelectCitys")[0].value, TimeFrom, TimeTo);
        console.log(Item)
        let Citys = Item.data.records.locations[0].location.map((item, i) =>
            <option key={i} value={i}>{item.locationName}</option>);
        ReactDOM.render(
            <>{Citys}</>, SelectArea
        );

        setTimeout(() => {
            let MaxATs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[5].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>最高溫度:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{MaxATs}</>, document.getElementsByClassName("MaxAT")[0]
            );

            let MinTs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[8].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>最低溫度:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{MinTs}</>, document.getElementsByClassName("MinT")[0]
            );

            let POPs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[0].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>降雨機率:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{POPs}</>, document.getElementsByClassName("POP")[0]
            );
            let UVIs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[9].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>紫外線指數:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{UVIs}</>, document.getElementsByClassName("UVI")[0]
            );

            let Wxs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[6].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>天氣現象:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{Wxs}</>, document.getElementsByClassName("Wx")[0]
            );
        }, 500);
    } catch (error) {
        console.log("異常", error);
    }
}

export function Weather() {
    setTimeout(() => {
        Day12Hrs(Today);
    }, 100);
    return <section class="banner">
        <div class="container">
            <div id="scene">
                <div class="layer">
                    <img alt="" src={mountains} />
                </div>
                <div class="layer">
                    <img class="sun" alt="" src={sun} />
                </div>
                <h1 data-depth="0.1">測試</h1>
            </div>
            <div class="wave">
                {/* <div>
                    <svg class="waves" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"
                        viewBox="0 24 150 28" preserveAspectRatio="none" shape-rendering="auto">
                        <defs>
                            <path id="gentle-wave"
                                d="M-160 44c30 0 58-18 88-18s 58 18 88 18 58-18 88-18 58 18 88 18 v44h-352z" />
                        </defs>
                        <g class="parallax">
                            <use xlink:href="#gentle-wave" x="48" y="0" fill="rgba(255,255,255,0.7" />
                            <use xlink:href="#gentle-wave" x="48" y="3" fill="rgba(255,255,255,0.5)" />
                            <use xlink:href="#gentle-wave" x="48" y="5" fill="rgba(255,255,255,0.3)" />
                            <use xlink:href="#gentle-wave" x="48" y="7" fill="#fff" />
                        </g>
                    </svg>
                </div> */}
            </div>
        </div>
    </section>
}





