//增加公司資料
import React from "react";
import CustomInput from "../Store/StoreCustomInput";
// import EasyCrop from "../ReactElement/easyCrop/index";
function StoreCompanyInfo({
  handelError,
  handelChange,
  companyData,
  companyDataError,
  postStoreIMGAPI,
  ContactLogoRef,
}) {
  return (
    <div onChange={handelError}>
      <div className="storeCreatGroup">
        <div className="storeCreatTextTitle">
          <p>新增公司資料</p>
        </div>
        <div className="storeCreatTextBody">
          <CustomInput
            title={"公司名稱"}
            required={true}
            inputName={"storeCompanyName"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
          />
          <CustomInput
            title={"統一編號"}
            required={true}
            inputName={"storeUNumbers"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
            maxlength="8"
          />
          <CustomInput
            title={"食品業者登錄字號"}
            required={true}
            inputName={"storeFoodNumber"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
            maxlength="19"
          />

          <CustomInput
            title={"聯絡人姓名"}
            required={true}
            inputName={"storeCompanyContactName"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
          />
          <CustomInput
            title={"聯絡人電話"}
            required={true}
            inputName={"storeCompanyContactCell"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
            maxlength="10"
          />

          <CustomInput
            title={"聯絡人信箱"}
            required={true}
            inputName={"storeCompanyContactEmail"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
          />
          <CustomInput
            title={"負責人姓名"}
            required={true}
            inputName={"storePrincipalName"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
          />
          <CustomInput
            title={"負責人身分證字號"}
            required={true}
            inputName={"storePrincipalNniform"}
            handelChange={handelChange}
            companyData={companyData}
            // formData={formData}
            companyDataError={companyDataError}
            maxlength="10"
          />

          <div className="storeCreatTextItem col-12 col-xl-4">
            <h6>
              公司商標<span style={{ color: "red" }}>*</span>
            </h6>
            <input
              ref={ContactLogoRef}
              className="ps-0"
              type="file"
              accept="image/png, image/jpeg"
              required={true}
              placeholder="請輸入"
              name="storeCompanyContactLogo"
              onChange={postStoreIMGAPI}
              style={{
                borderColor: companyDataError.storeCompanyContactLogo
                  ? "red"
                  : "",
              }}
            />
            <br />
            <small style={{ color: "#768192" }}>建議尺寸 500px*500px</small>
            <br />
            {companyDataError.storeCompanyContactLogo ? (
              <span style={{ color: "red" }}>請上傳公司商標</span>
            ) : (
              ""
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default StoreCompanyInfo;
