import React, { useEffect } from "react";
import "../../../styles/Finance/Finance.css"
import { DateInput } from "../../../components/ReactElement/InputCombo"
import { ColorBtn } from "../../../components/ReactElement/ColorBtn"
// 引入信用卡元件
import { CreditCard } from "../../../components/ReactElement/CreditCard/CreditCard"
// 引入表格
import { LabelTitleNormal } from "../../../components/ReactElement/OriginalLabel/LabelTitle"
import { LabelCheckerboard } from "../../../components/ReactElement/OriginalLabel/LabelCheckerboard"
import { PageBtnsHader, PageBtnsFooter } from "../../../components/ReactElement/OriginalLabel/LabelPageBtns"
// 引入表格需要的函式
import { ChangeWorld } from "./LabelFunction"
import { PageSelect } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function Finance() {
    let DataTitle = ["排序", "日期", "匯款廠商", "匯款金額"];
    localStorage.setItem("LabelTitleCost", DataTitle.length, 60000) //製造表單標題格數
    useEffect(() => {
        setTimeout(() => {
            ChangeWorld();
            PageSelect();
        }, 100);
    });
    if (localStorage.MaxPageValue === undefined) {
        localStorage.setItem("MaxPageValue", 10, 60000)
    }
    localStorage.setItem("LabelCotentCost", localStorage.MaxPageValue, 60000) //製造表單欄位格數 
    return <div>
        {/* 信用卡特區 */}
        <CreditCard />

        {/* 日期選擇框 */}
        <div className="FinanceInputs">
            <div className="FinanceFlexDiv">
                <DateInput p="日期起訖" range="~" dateText="開始日期" />
                <DateInput PClassName="DateInputP" dateText="結束日期" />
            </div>
            <div className="FinanceFlexDiv">
                <ColorBtn
                    text="搜尋"
                    backgroundColor="#1985AC" />
            </div>
        </div>
        < PageBtnsHader />
        <table className="ChienLiLabel">
            <LabelTitleNormal />
            <LabelCheckerboard />
        </table>
        <PageBtnsFooter />
    </div>
}
