import { BigWhiteBtn } from "../../../components/ReactElement/ColorBtn"
import { ReactComponent as TotalRevenueIcon } from "../../../../src/images/svg/ui-icon/總營收icon.svg";
import { ReactComponent as TotalRevenueIcon2Icon } from "../../../../src/images/svg/ui-icon/總營收2icon.svg";
import { ReactComponent as AverageAmountIcon } from "../../../../src/images/svg/ui-icon/平均訂單金額icon.svg";
import { ReactComponent as TotalOrderIcon } from "../../../../src/images/svg/ui-icon/總訂單數icon.svg";
export function BigWhiteBtns() {

    return <div className="BigWhiteBtns">
        <div className="TwinsBigWhiteBtn">
            <BigWhiteBtn
                onClick={() => {
                    alert(1);
                }}
                onMouseMove={() => {
                    whichBigWhiteMore(0);
                }}
                onMouseOut={() => {
                    whichBigWhiteRecovery(0);
                }}
                icon={<TotalRevenueIcon />} title="總營收" amount="$35,210" />
            <BigWhiteBtn
                onClick={() => {
                    alert(2);
                }}
                onMouseMove={() => {
                    whichBigWhiteMore(1);
                }}
                onMouseOut={() => {
                    whichBigWhiteRecovery(1);
                }}
                icon={<TotalRevenueIcon2Icon />} title="折扣額" amount="$60,000" />
        </div>

        <div className="TwinsBigWhiteBtn">
            <BigWhiteBtn
                onClick={() => {
                    alert(3);
                }}
                onMouseMove={() => {
                    whichBigWhiteMore(2);
                }}
                onMouseOut={() => {
                    whichBigWhiteRecovery(2);
                }}
                icon={<AverageAmountIcon />} title="平均訂單金額" amount="$200" />
            <BigWhiteBtn
                onClick={() => {
                    alert(4);
                }}
                onMouseMove={() => {
                    whichBigWhiteMore(3);
                }}
                onMouseOut={() => {
                    whichBigWhiteRecovery(3);
                }}
                icon={<TotalOrderIcon />} title="總訂單數" amount="777" />
        </div>
    </div>
}

function whichBigWhiteMore(x) {
    let likeMove = true;
    DotsEffect(x, likeMove);
}

function whichBigWhiteRecovery(x) {
    let likeMove = false;
    DotsEffect(x, likeMove);
}

function DotsEffect(whitch, likeMove) {
    let BigWhiteMore = document.getElementsByClassName("BigWhiteMore");
    let Dots = BigWhiteMore[whitch].getElementsByClassName("BtnDot");
    let BtnDotJump = document.getElementsByClassName("BtnDotJump");
    if (likeMove === true) {
        for (let x = 0; x < Dots.length; x++) {
            Dots[x].style.display = "none";
            BtnDotJump[whitch].style.display = "block";
        }
    }
    else {
        for (let x = 0; x < Dots.length; x++) {
            Dots[x].style.display = "block";
            BtnDotJump[whitch].style.display = "none";
        }
    }
}
